CREATE OR REPLACE PROCEDURE pls_atualizar_conta_historico ( nr_seq_segurado_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	update	pls_conta
        set     nr_seq_segurado	= nr_seq_segurado_p,
		nm_usuario	= nm_usuario_p
	where   nr_sequencia  	= nr_sequencia_p;

	insert into
	pls_conta_historico(
                nr_sequencia,
                dt_atualizacao,
                nm_usuario,
                nr_seq_conta,
                dt_historico,
                ds_titulo)
	values (
		nextval('pls_conta_historico_seq'),
                clock_timestamp(),
                nm_usuario_p,
                nr_sequencia_p,
                clock_timestamp(),
                'Alterado o benefici√°rio da conta.');
	end;
end if;
commit;
end;
$body$
