SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_conta_proc ( nr_seq_conta_proc_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_glosa_w			bigint;
vl_procedimento_w			double precision;
nr_seq_conta_w			bigint;


BEGIN
select	count(*)
into STRICT	qt_glosa_w
from	pls_conta_glosa
where	nr_seq_conta_proc	= nr_seq_conta_proc_p
and	ie_situacao = 'A';

select	vl_procedimento,
	nr_seq_conta
into STRICT	vl_procedimento_w,
	nr_seq_conta_w
from	pls_conta_proc
where	nr_sequencia	= nr_seq_conta_proc_p;

update	pls_conta
set	ie_status	= 'P'
where	nr_sequencia	= nr_seq_conta_w;

update	pls_conta_proc
set	vl_liberado	= vl_procedimento_w,
	ie_status	= 'P'
where	nr_sequencia	= nr_seq_conta_proc_p
and	ie_status <> 'D';

if (qt_glosa_w = 0) then
	update	pls_conta_proc
	set	vl_liberado	= vl_procedimento_w,
		ie_status	= 'S'
	where	nr_sequencia	= nr_seq_conta_proc_p
	and	ie_status <> 'D';

	select	count(1)
	into STRICT	qt_glosa_w
	from	pls_conta_glosa
	where	nr_seq_conta	= nr_seq_conta_w
	and	ie_situacao = 'A'  LIMIT 1;

	if (qt_glosa_w = 0) then
		select	count(*)
		into STRICT	qt_glosa_w
		from	pls_conta_glosa  a,
			pls_conta_proc    b
		where	a.nr_seq_conta_proc	= b.nr_sequencia
		and	b.nr_seq_conta		= nr_seq_conta_w
		and	a.ie_situacao = 'A';
	end if;

	if (qt_glosa_w = 0) then
		select	count(*)
		into STRICT	qt_glosa_w
		from	pls_conta_glosa  a,
			pls_conta_mat    b
		where	a.nr_seq_conta_mat	= b.nr_sequencia
		and	b.nr_seq_conta		= nr_seq_conta_w
		and	a.ie_situacao = 'A';
	end if;

	if (qt_glosa_w	= 0) then
		update	pls_conta
		set	ie_status	= 'L'
		where	nr_sequencia	= nr_seq_conta_w;
	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
