CREATE OR REPLACE PROCEDURE pls_atualizar_cta_contab_desp ( dt_referencia_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, cd_tipo_lote_contabil_p bigint, ie_geracao_p INOUT text) AS $body$
DECLARE


cd_tipo_lote_contabil_w			tipo_lote_contabil.cd_tipo_lote_contabil%type;

c_tipo_lote CURSOR FOR
	SELECT	cd_tipo_lote_contabil
	from	tipo_lote_contabil
	where	((cd_tipo_lote_contabil(22,40,41,33,42,43)
	and (cd_tipo_lote_contabil_p = 0))
	or (cd_tipo_lote_contabil = cd_tipo_lote_contabil_p));


BEGIN

open c_tipo_lote;
loop
fetch c_tipo_lote into
	cd_tipo_lote_contabil_w;
EXIT WHEN NOT FOUND; /* apply on c_tipo_lote */
	begin

	ie_geracao_p := pls_atualizar_contas_contabeis(dt_referencia_p, nm_usuario_p, cd_estabelecimento_p, cd_tipo_lote_contabil_w, ie_geracao_p);

	end;
end loop;
close c_tipo_lote;

commit;

end;
