SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_dados_contas ( ds_lista_nr_conta_p text, nr_seq_prestador_exec_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_nr_conta_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_sequencia_w	bigint;


BEGIN
if (ds_lista_nr_conta_p IS NOT NULL AND ds_lista_nr_conta_p::text <> '') and (nr_seq_prestador_exec_p IS NOT NULL AND nr_seq_prestador_exec_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_nr_conta_w := ds_lista_nr_conta_p;

	while(ds_lista_nr_conta_w IS NOT NULL AND ds_lista_nr_conta_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_nr_conta_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_sequencia_w	:= (substr(ds_lista_nr_conta_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_nr_conta_w	:= substr(ds_lista_nr_conta_w,nr_pos_virgula_w+1,length(ds_lista_nr_conta_w));
			end;
		else
			begin
			nr_seq_sequencia_w	:= (ds_lista_nr_conta_w)::numeric;
			ds_lista_nr_conta_w	:= null;
			end;
		end if;

		if (nr_seq_sequencia_w > 0) then
			begin
			update	pls_conta
			set	nr_seq_prestador_exec 	= nr_seq_prestador_exec_p,
				nm_usuario 		= nm_usuario_p
			where   nr_sequencia 		= nr_seq_sequencia_w;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
