CREATE OR REPLACE PROCEDURE pls_atualizar_dados_imp_a900 ( nr_seq_lote_imp_p bigint, nm_arquivo_p text, ie_opcao_p text) AS $body$
DECLARE


/* IE_OPCAO_P
DII - Data de início da importação
DFI - Data final da importação
DIP - Data início do processamento
DFP - Data final do processamento */
BEGIN
if (nm_arquivo_p IS NOT NULL AND nm_arquivo_p::text <> '') then
	update	pls_lote_imp_mat_unimed
	set	nm_arquivo 	= nm_arquivo_p
	where	nr_sequencia	= nr_seq_lote_imp_p;
end if;

if (upper(ie_opcao_p) = 'DII') then
	update	pls_lote_imp_mat_unimed
	set	dt_inicio_importacao 	= clock_timestamp()
	where	nr_sequencia		= nr_seq_lote_imp_p;

elsif (upper(ie_opcao_p) = 'DFI') then
	update	pls_lote_imp_mat_unimed
	set	dt_fim_importacao 	= clock_timestamp()
	where	nr_sequencia		= nr_seq_lote_imp_p;

elsif (upper(ie_opcao_p) = 'DIP') then
	update	pls_lote_imp_mat_unimed
	set	dt_inicio_processamento = clock_timestamp()
	where	nr_sequencia		= nr_seq_lote_imp_p;

elsif (upper(ie_opcao_p) = 'DFP') then
	update	pls_lote_imp_mat_unimed
	set	dt_fim_processamento 	= clock_timestamp()
	where	nr_sequencia		= nr_seq_lote_imp_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
