CREATE OR REPLACE PROCEDURE pls_atualizar_dados_pos_estab (nr_seq_analise_p bigint, ie_opcao_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_conta_proc_w	bigint;
nr_seq_conta_mat_w	bigint;
nr_sequencia_w		bigint;
nr_seq_conta_partic_w	bigint;
qt_item_w		double precision;
					
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_conta_proc, 
		null 
	from	w_pls_resumo_conta 
	where	nr_seq_analise = nr_seq_analise_p 
	
union
 
	SELECT	nr_sequencia, 
		null, 
		nr_seq_conta_mat 
	from	w_pls_resumo_conta 
	where	nr_seq_analise = nr_seq_analise_p;
										

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w, 
	nr_seq_conta_proc_w, 
	nr_seq_conta_mat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_conta_partic_w 
	from	pls_conta_pos_estabelecido 
	where	nr_seq_conta_proc = nr_seq_conta_proc_w 
	or	nr_seq_conta_mat = nr_seq_conta_mat_w 
	and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = ''));
	 
	select	max(qt_apresentado) 
	into STRICT	qt_item_w 
	from	w_pls_resumo_conta 
	where	nr_sequencia = nr_sequencia_w;
	 
	update	pls_conta_pos_estabelecido 
	set	qt_item		= qt_item_w, 
		dt_atualizacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_Sequencia 	= nr_seq_conta_partic_w;
	 
	end;
end loop;
close C01;
 
-- Atualizar os dados do participantes 
CALL pls_atualizar_dados_partic_pos(nr_seq_analise_p,null,'N',nm_usuario_p);
 
if (coalesce(ie_commit_p,'N') = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
