CREATE OR REPLACE PROCEDURE pls_atualizar_dados_req_web ( nr_seq_requisicao_p bigint, ds_indicacao_p text, nr_seq_tipo_acomodacao_p bigint, qt_dia_solicitado_p bigint, ds_observacao_p text, ie_tipo_guia_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar os dados na requisição.

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_analise_w	pls_auditoria.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_analise_w
from	pls_auditoria
where	nr_seq_requisicao = nr_seq_requisicao_p;

if (ie_tipo_guia_p = 8) then
	update	pls_requisicao
	set	ds_indicacao_clinica 	= ds_indicacao_p,
		nr_seq_tipo_acomodacao 	= nr_seq_tipo_acomodacao_p,
		qt_dia_solicitado 	= qt_dia_solicitado_p
	where	nr_sequencia 		= nr_seq_requisicao_p;

	if (nr_seq_analise_w IS NOT NULL AND nr_seq_analise_w::text <> '') then
		update	pls_auditoria
		set	ds_indicacao_clinica	= ds_indicacao_p
		where	nr_sequencia		= nr_seq_analise_w;
	end if;
else
	update	pls_requisicao
	set	ds_indicacao_clinica 	= ds_indicacao_p,
		ds_observacao	 	= ds_observacao_p
	where	nr_sequencia 		= nr_seq_requisicao_p;

	if (nr_seq_analise_w IS NOT NULL AND nr_seq_analise_w::text <> '') then
		update	pls_auditoria
		set	ds_indicacao_clinica	= ds_indicacao_p,
			ds_observacao	 	= ds_observacao_p
		where	nr_sequencia		= nr_seq_analise_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
