CREATE OR REPLACE PROCEDURE pls_atualizar_dias_carencia ( nr_seq_carencia_p bigint, qt_dias_p bigint, ie_tipo_carencia_p text) AS $body$
BEGIN
if (qt_dias_p IS NOT NULL AND qt_dias_p::text <> '') and (nr_seq_carencia_p IS NOT NULL AND nr_seq_carencia_p::text <> '') then
	if (ie_tipo_carencia_p = 'B') then
		update	pls_carencia
		set		qt_dias			= qt_dias_p,
				dt_fim_vigencia	= dt_inicio_vigencia + qt_dias_p
		where	nr_sequencia	= nr_seq_carencia_p;
		commit;
	elsif (ie_tipo_carencia_p = 'P') then
		update	pls_carencia
		set		qt_dias			= qt_dias_p
		where	nr_sequencia	= nr_seq_carencia_p;
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
