CREATE OR REPLACE PROCEDURE pls_atualizar_dias_reduzidos ( nr_seq_carencia_p bigint, qt_dias_ant_p bigint, qt_dias_p bigint, nm_usuario_p text ) AS $body$
BEGIN

if (qt_dias_p < qt_dias_ant_p) then
	update	pls_carencia
	set	qt_dias_reducao_man = coalesce(qt_dias_reducao_man,0) + (qt_dias_ant_p - qt_dias_p)
	where	nr_sequencia = nr_seq_carencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
