CREATE OR REPLACE PROCEDURE pls_atualizar_dt_val_req_web ( nr_seq_requisicao_p bigint, dt_validade_p text, nm_usuario_p text) AS $body$
BEGIN
	if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
		update  pls_requisicao
		set	dt_validade_senha = to_date(dt_validade_p,'dd/mm/yyyy'),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_requisicao_p;
	end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
