SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_familia_pf ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_titular_w		bigint;
cd_matricula_familia_w		bigint;
nr_seq_regra_w			bigint;
cd_atual_w			bigint;
cd_inicial_w			bigint;
cd_final_w			bigint;
cd_regra_w			bigint;
cd_matricula_familia_ww		bigint;
cd_operadora_empresa_w		bigint;
ie_tipo_incremento_w		varchar(2);
nr_seq_contrato_w		bigint;
nr_seq_plano_w			bigint;
nr_seq_intercambio_w		bigint;
ie_tipo_pessoa_w		varchar(10);
nr_seq_grupo_intercambio_w	bigint;
dt_importacao_arquivo_w		timestamp;
ie_tipo_contrato_w		varchar(10);
ie_acao_contrato_w		varchar(2);
cd_matricula_w			bigint;
ie_tipo_contratacao_w		pls_plano.ie_tipo_contratacao%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_carteira_controle_iden
	where	ie_tipo_regra		= 'F'
	and	ie_situacao		= 'A'
	and	((nr_seq_plano		= nr_seq_plano_w and (nr_seq_plano IS NOT NULL AND nr_seq_plano::text <> '')) or (coalesce(nr_seq_plano::text, '') = ''))
	and	((nr_seq_grupo_intercambio = nr_seq_grupo_intercambio_w) or (coalesce(nr_seq_grupo_intercambio::text, '') = ''))
	and	((coalesce(ie_tipo_pessoa,'A') = ie_tipo_pessoa_w) or (coalesce(ie_tipo_pessoa,'A') = 'A'))
	and	((ie_tipo_contrato	= ie_tipo_contrato_w) or (coalesce(ie_tipo_contrato::text, '') = ''))
	and	((coalesce(ie_tipo_contratacao::text, '') = '') or (ie_tipo_contratacao = ie_tipo_contratacao_w))
	and	cd_estabelecimento	= cd_estabelecimento_p
	order by	
		coalesce(nr_seq_grupo_intercambio,0),
		coalesce(nr_seq_plano,0),
		CASE WHEN coalesce(ie_tipo_contrato::text, '') = '' THEN -1  ELSE 1 END ,
		coalesce(ie_tipo_pessoa,'A'),
		ie_tipo_contratacao;


BEGIN
dt_importacao_arquivo_w	:= null;

select	a.nr_seq_titular,
	a.cd_matricula_familia,
	a.nr_seq_plano,
	a.nr_seq_contrato,
	a.nr_seq_intercambio,
	a.nr_seq_grupo_intercambio,
	a.ie_acao_contrato,
	a.cd_operadora_empresa,
	(select	max(ie_tipo_contratacao)
	from	pls_plano x
	where	x.nr_sequencia = a.nr_seq_plano)
into STRICT	nr_seq_titular_w,
	cd_matricula_familia_ww,
	nr_seq_plano_w,
	nr_seq_contrato_w,
	nr_seq_intercambio_w,
	nr_seq_grupo_intercambio_w,
	ie_acao_contrato_w,
	cd_operadora_empresa_w,
	ie_tipo_contratacao_w
from	pls_segurado a
where	a.nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
	select	CASE WHEN coalesce(cd_pf_estipulante::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	ie_tipo_pessoa_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
	
	select	max(d.dt_importacao_arquivo)
	into STRICT	dt_importacao_arquivo_w
	from	pls_segurado			a,
		pls_proposta_beneficiario	b,
		pls_inclusao_beneficiario	c,
		pls_lote_inclusao_benef		d
	where	a.nr_seq_pessoa_proposta	= b.nr_sequencia
	and	c.nr_seq_proposta_benef		= b.nr_sequencia
	and	c.nr_seq_lote_inclusao		= d.nr_sequencia
	and	a.nr_sequencia			= nr_seq_segurado_p;
	
	ie_tipo_contrato_w	:= 'O';
	
elsif (nr_seq_intercambio_w IS NOT NULL AND nr_seq_intercambio_w::text <> '') then
	select	CASE WHEN coalesce(cd_cgc::text, '') = '' THEN 'PF'  ELSE 'PJ' END
	into STRICT	ie_tipo_pessoa_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_w;
	
	select	max(c.dt_importacao_arquivo)
	into STRICT	dt_importacao_arquivo_w
	from	pls_segurado			a,
		pls_inclusao_beneficiario	b,
		pls_lote_inclusao_benef		c
	where	a.nr_seq_benef_inclusao	= b.nr_sequencia
	and	b.nr_seq_lote_inclusao	= c.nr_sequencia
	and	a.nr_sequencia		= nr_seq_segurado_p;
	
	ie_tipo_contrato_w	:= 'I';
end if;

if (coalesce(nr_seq_titular_w,0)	= 0) then /* Se o beneficiario e dependente */
	nr_seq_titular_w	:= nr_seq_segurado_p;
end if;

select	cd_matricula_familia
into STRICT	cd_matricula_familia_w
from	pls_segurado
where	nr_sequencia	= nr_seq_titular_w;

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (coalesce(nr_seq_regra_w,0) <> 0) then
	begin
	select	coalesce(ie_tipo_incremento,'SQ')
	into STRICT	ie_tipo_incremento_w
	from	pls_carteira_controle_iden
	where	nr_sequencia	= nr_seq_regra_w;
	exception
	when others then
		ie_tipo_incremento_w	:= 'SQ';
	end;
	
	if (coalesce(dt_importacao_arquivo_w::text, '') = '') then
		cd_matricula_w := pls_buscar_familia_pf( 	ie_tipo_incremento_w, cd_matricula_familia_w, cd_matricula_familia_ww, nr_seq_regra_w, nr_seq_contrato_w, nr_seq_segurado_p, nr_seq_intercambio_w, nm_usuario_p, cd_operadora_empresa_w, cd_matricula_w);
	end if;
	
	if (cd_matricula_w IS NOT NULL AND cd_matricula_w::text <> '') then
		update	pls_segurado
		set	cd_matricula_familia	= cd_matricula_w
		where	((nr_seq_titular	= nr_seq_segurado_p) or (nr_sequencia	= nr_seq_segurado_p));
	end if;
else
	if (coalesce(nr_seq_regra_w,0) = 0  and (cd_matricula_familia_w IS NOT NULL AND cd_matricula_familia_w::text <> ''))then
		update	pls_segurado
		set	cd_matricula_familia	= cd_matricula_familia_w
		where	nr_sequencia		= nr_seq_segurado_p;
	end if;
end if;

--  commit;  NAO PODE DAR COMMIT, UTILIZADO NA APROVACAO DO CONTRATO
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_familia_pf ( nr_seq_segurado_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

