CREATE OR REPLACE PROCEDURE pls_atualizar_grupo_ans ( nr_seq_atualizacao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
dt_inicial_w		timestamp;
dt_final_w		timestamp;
nr_seq_movimento_w	bigint;
cd_tipo_lote_contabil_w	pls_atualizacao_contabil.cd_tipo_lote_contabil%type;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_movimento_contabil a 
	where	a.nr_seq_atualizacao	= nr_seq_atualizacao_p 
	and	exists (	SELECT	1 
				from	pls_movto_contab_inconsist x 
				where	x.nr_seq_movimento = a.nr_sequencia 
				and	x.nr_seq_inconsistencia = 3);


BEGIN 
 
select	trunc(dt_mes_competencia,'month'), 
	last_day(dt_mes_competencia) 
into STRICT	dt_inicial_w, 
	dt_final_w 
from	pls_atualizacao_contabil 
where	nr_sequencia	= nr_seq_atualizacao_p;
 
CALL pls_atualizar_grupo_ans_conta(0, 'N', dt_inicial_w, dt_final_w, 'S', nm_usuario_p, cd_estabelecimento_p, nr_seq_atualizacao_p);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_movimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL pls_atualizar_contas_item(nr_seq_movimento_w, nm_usuario_p, cd_estabelecimento_p);
	end;
end loop;
close C01;
 
update	pls_competencia 
set	dt_atualizacao_grupo	= clock_timestamp() 
where	trunc(dt_mes_competencia,'month') = trunc(dt_inicial_w,'month') 
and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
