CREATE OR REPLACE PROCEDURE pls_atualizar_grupo_penden ( nr_seq_analise_p pls_analise_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_grupo_w	pls_auditoria_conta_grupo.nr_seq_grupo%type;


BEGIN

select	max(nr_seq_grupo)
into STRICT	nr_seq_grupo_w
from	pls_auditoria_conta_grupo
where	nr_seq_analise	= nr_seq_analise_p
and	nr_seq_ordem 	= (	SELECT	min(nr_seq_ordem)
				from	pls_auditoria_conta_grupo
				where	nr_seq_analise = nr_seq_analise_p
				and	coalesce(dt_liberacao::text, '') = '')
and	coalesce(dt_liberacao::text, '') = '';

update	pls_analise_conta
set	nr_seq_grupo_auditor	= nr_seq_grupo_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_analise_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
