SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_guia_web ( nr_seq_guia_p bigint, cd_operadora_exec_p text, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Atualizar a guia de atendimento
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/cd_estabelecimento_w		bigint;
ie_tipo_processo_w		varchar(4);
nr_seq_uni_exec_w		bigint;
cd_matricula_estipulante_w	varchar(30);
nr_seq_congenere_w		bigint;
qt_registros_w			bigint := 0;
nr_seq_segurado_w		numeric(20);
nr_seq_cat_w			bigint;


BEGIN


if ((cd_operadora_exec_p IS NOT NULL AND cd_operadora_exec_p::text <> '') and cd_operadora_exec_p <> 'X') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_uni_exec_w
	from	pls_congenere
	where	cd_cooperativa	= cd_operadora_exec_p;

	if (nr_seq_uni_exec_w IS NOT NULL AND nr_seq_uni_exec_w::text <> '') then
		update	pls_guia_plano
		set	nr_seq_uni_exec	= nr_seq_uni_exec_w
		where	nr_sequencia		= nr_seq_guia_p;
	end if;
end if;

begin
	select	cd_estabelecimento,
		nr_seq_segurado,
		nr_seq_cat
	into STRICT	cd_estabelecimento_w,
		nr_seq_segurado_w,
		nr_seq_cat_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;
exception
when others then
	cd_estabelecimento_w	:= 0;
	nr_seq_segurado_w := null;
	nr_seq_cat_w	:= null;
end;

if (cd_estabelecimento_w	<> 0) then
	ie_tipo_processo_w	:= substr(pls_obter_tipo_processo_web(nr_seq_guia_p, null, cd_estabelecimento_w),1,255);
end if;

if (ie_tipo_processo_w	= 'I') then
	update	pls_guia_plano
	set	ie_tipo_processo	= ie_tipo_processo_w,
		ie_tipo_intercambio     = 'I'
	where	nr_sequencia		= nr_seq_guia_p;

	CALL pls_atualiza_proc_guia_web(nr_seq_guia_p, nm_usuario_p);
end if;

-- validação do código de matricula do beneficiário
if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
	begin
		select	nr_seq_congenere,
			cd_matricula_estipulante
		into STRICT	nr_seq_congenere_w,
			cd_matricula_estipulante_w
		from	pls_segurado
		where	nr_sequencia = nr_seq_segurado_w;
	exception
	when others then
		nr_seq_congenere_w := null;
		cd_matricula_estipulante_w := null;
	end;

	if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
		select  count(1)
		into STRICT	qt_registros_w
		from	pls_congenere
		where	ie_tipo_congenere = 'OP'
		and	nr_sequencia = nr_seq_congenere_w;

		if (qt_registros_w > 0 and (cd_matricula_estipulante_w IS NOT NULL AND cd_matricula_estipulante_w::text <> '')) then
			update	pls_guia_plano
			set	cd_matricula_estipulante = cd_matricula_estipulante_w
			where	nr_sequencia		 = nr_seq_guia_p;
		end if;
	end if;
end if;

if (nr_seq_cat_w IS NOT NULL AND nr_seq_cat_w::text <> '') then
	CALL pls_gerar_atend_guia_cat_web(	nr_seq_guia_p, nr_seq_cat_w, null,
					null,null,nm_usuario_p,
					null,null,null,
					null,null,null,
					null,null,null,
					null,null,null,
					null);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_guia_web ( nr_seq_guia_p bigint, cd_operadora_exec_p text, nm_usuario_p text) FROM PUBLIC;

