SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_infor_nf_ptu ( nr_seq_ptu_fatura_p ptu_fatura.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text, ie_atualizar_reg_nf_a500_p pls_parametros.ie_atualizar_reg_nf_a500%type default null) AS $body$
DECLARE

					
nr_seq_nota_cobr_w		ptu_nota_cobranca.nr_sequencia%type;
nr_nota_fiscal_r506_w		ptu_nota_fiscal.nr_nota_fiscal%type;
nr_nota_fiscal_doc2_r506_w	ptu_nota_fiscal.nr_nota_fiscal_doc2%type;
ie_tipo_cobranca_fatura_w	ptu_fatura.ie_tipo_cobranca_fatura%type;


BEGIN
-- Remover a informacao de nota fiscal do A500
delete	FROM ptu_nota_fiscal
where	nr_seq_fatura = nr_seq_ptu_fatura_p;

select	max(nr_sequencia)
into STRICT	nr_seq_nota_cobr_w
from	ptu_nota_cobranca
where	nr_seq_fatura	= nr_seq_ptu_fatura_p;

select	coalesce(max(ie_tipo_cobranca_fatura), 'C')
into STRICT	ie_tipo_cobranca_fatura_w
from	ptu_fatura
where	nr_sequencia	= nr_seq_ptu_fatura_p;

-- Gerar a informacao de nota fiscal do A500 (se nao for aviso)
if (ie_tipo_cobranca_fatura_w != 'A') then

	CALL pls_gerar_ptu_nota_fiscal( nr_seq_nota_cobr_w, nr_seq_ptu_fatura_p, nm_usuario_p);
end if;


if (ie_atualizar_reg_nf_a500_p = 'D') then
	select	max(nr_nota_fiscal),
		max(nr_nota_fiscal_doc2)
	into STRICT	nr_nota_fiscal_r506_w,
		nr_nota_fiscal_doc2_r506_w
	from	ptu_nota_fiscal
	where	nr_seq_fatura = nr_seq_ptu_fatura_p;
		
	if (nr_nota_fiscal_r506_w IS NOT NULL AND nr_nota_fiscal_r506_w::text <> '') or (nr_nota_fiscal_doc2_r506_w IS NOT NULL AND nr_nota_fiscal_doc2_r506_w::text <> '') then
		update	ptu_fatura
		set	nr_fatura		= nr_nota_fiscal_r506_w,
			doc_fiscal_1		= nr_nota_fiscal_r506_w,
			nr_nota_credito_debito	= nr_nota_fiscal_doc2_r506_w,
			doc_fiscal_2		= nr_nota_fiscal_doc2_r506_w
		where	nr_sequencia		= nr_seq_ptu_fatura_p;
	end if;
end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
