CREATE OR REPLACE PROCEDURE pls_atualizar_item_req_web ( nr_seq_item_p bigint, qt_item_p bigint, ie_tipo_p text, vl_material_p bigint, nm_usuario_p text, ds_observacao_p text) AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Atualizar a quantidade do item na requisição (procedimento / material)
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

	IE_TIPO_P
	P - procedimento
	M - material
*/BEGIN


if (ie_tipo_p = 'P') then
	update	pls_requisicao_proc
	set		qt_solicitado 	= qt_item_p,
			ds_observacao	= ds_observacao_p
	where	nr_sequencia 	= nr_seq_item_p;
else
	update	pls_requisicao_mat
	set		qt_solicitado	= qt_item_p,
			vl_material		= vl_material_p,
			ds_observacao	= ds_observacao_p
	where	nr_sequencia 	= nr_seq_item_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
