CREATE OR REPLACE PROCEDURE pls_atualizar_just_aud_itens ( ds_lista_itens_p text, nr_seq_aud_item_p bigint, ie_origem_p text, ie_origem_itens_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_itens_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_aud_item_w	bigint;


BEGIN
if (ds_lista_itens_p IS NOT NULL AND ds_lista_itens_p::text <> '') and (nr_seq_aud_item_p IS NOT NULL AND nr_seq_aud_item_p::text <> '') and (ie_origem_p IS NOT NULL AND ie_origem_p::text <> '') and (ie_origem_itens_p IS NOT NULL AND ie_origem_itens_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_itens_w	:= ds_lista_itens_p;

	while(ds_lista_itens_w IS NOT NULL AND ds_lista_itens_w::text <> '') loop
		begin

		nr_pos_virgula_w	:= position(',' in ds_lista_itens_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_aud_item_w	:= (substr(ds_lista_itens_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_itens_w	:= substr(ds_lista_itens_w,nr_pos_virgula_w+1,length(ds_lista_itens_w));
			end;
		else
			begin
			nr_seq_aud_item_w	:= (ds_lista_itens_w)::numeric;
			ds_lista_itens_w	:= null;
			end;
		end if;

		if (nr_seq_aud_item_w > 0) then
			begin

			CALL pls_atualizar_status_justifica(
				nr_seq_aud_item_w,
				ie_origem_itens_p,
				'',
				nm_usuario_p);

			commit;

			end;
		end if;
		end;
		end loop;

	CALL pls_atualizar_status_justifica(
		nr_seq_aud_item_p,
		ie_origem_p,
		'',
		nm_usuario_p);

	end;
end if;
commit;
end;
$body$
