CREATE OR REPLACE PROCEDURE pls_atualizar_material_hosp ( cd_material_p bigint, nr_seq_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_material_w			varchar(255);
cd_tiss_brasindice_w		varchar(20);
qt_conversao_simpro_w		double precision;
qt_conversao_bras_w		double precision;
cd_simpro_w			bigint;


BEGIN
select	max(cd_simpro),
	max(qt_conversao)
into STRICT	cd_simpro_w,
	qt_conversao_simpro_w
from	material_simpro
where	cd_material	= cd_material_p;

select	max(substr(tiss_obter_cod_brasindice(cd_apresentacao, cd_laboratorio, cd_medicamento),1,15)),
	max(qt_conversao)
into STRICT	cd_tiss_brasindice_w,
	qt_conversao_bras_w
from	material_brasindice
where	cd_material	= cd_material_p;

update	pls_material
set	cd_simpro		= cd_simpro_w,
	qt_conversao_simpro	= qt_conversao_simpro_w,
	cd_tiss_brasindice	= cd_tiss_brasindice_w,
	qt_conversao		= qt_conversao_bras_w,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_material_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
