CREATE OR REPLACE PROCEDURE pls_atualizar_ordem_exec_tx ( nr_seq_regra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_ordem_execucao_regra_w	pls_regra_tx_opme.nr_ordem_execucao%type;


BEGIN
if (nr_seq_regra_p > 0) then
	select	substr(pls_obter_valor_prioridade(nr_seq_regra_p, 'PLS_REGRA_TX_OPME'),1,30)
	into STRICT	nr_ordem_execucao_regra_w
	;

	update	pls_regra_tx_opme
	set	nr_ordem_execucao	= nr_ordem_execucao_regra_w
	where	nr_sequencia		= nr_seq_regra_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
