CREATE OR REPLACE PROCEDURE pls_atualizar_pag_cartao ( nr_seq_pagador_fin_p bigint, nr_seq_pagamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_pagador_fin_w	pls_contrato_pagador_fin.nr_sequencia%type;	


BEGIN

select max(nr_sequencia)
into STRICT   nr_seq_pagador_fin_w
from   pls_contrato_pagador_fin
where  nr_seq_pagador = ( SELECT nr_seq_pagador
						  from	 pls_contrato_pagador_fin
						  where	 nr_sequencia = nr_seq_pagador_fin_p )
and		nr_seq_forma_cobranca = 5
and		nr_sequencia <> nr_seq_pagador_fin_p
and (coalesce(dt_fim_vigencia::text, '') = '' or dt_fim_vigencia >=clock_timestamp());


update	pls_solic_pagto_cartao_cr
set		nr_seq_pagador_fin = nr_seq_pagador_fin_w,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
where	nr_sequencia = nr_seq_pagamento_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
