SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_prest_pgto ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_proc_partic_p bigint, nr_seq_analise_p bigint, ie_comitar_p text, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nr_seq_prestador_pgto_w		bigint;
nr_seq_prestador_exec_w		bigint;
nr_seq_item_analise_w		bigint;
nm_prestador_pag_w		varchar(255);	
nr_seq_conta_w			pls_conta_proc_v.nr_seq_conta%type;
				

BEGIN 
nm_prestador_pag_w := '';
nr_seq_prestador_pgto_w := null;
nr_seq_prestador_exec_w	:= null;
nr_seq_item_analise_w	:= null;
if (coalesce(nr_seq_conta_proc_p,0) > 0) then 
 
	select	max(a.nr_seq_prestador_exec), 
		max(a.nr_sequencia) 
	into STRICT	nr_seq_prestador_exec_w, 
		nr_seq_conta_w 
	from	pls_conta a, 
		pls_conta_proc b 
	where	a.nr_sequencia = b.nr_seq_conta 
	and	b.nr_sequencia = nr_seq_conta_proc_p;
 
	select	max(nr_seq_prestador_pgto) 
	into STRICT	nr_seq_prestador_pgto_w 
	from	pls_conta_medica_resumo 
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p 
	and 	nr_seq_conta		= nr_seq_conta_w 
	and	((ie_situacao != 'I') or (coalesce(ie_situacao::text, '') = ''));
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_item_analise_w 
	from	w_pls_resumo_conta 
	where	nr_seq_analise		= nr_seq_analise_p 
	and	nr_seq_conta_proc	= nr_seq_conta_proc_p;
 
elsif (coalesce(nr_seq_conta_mat_p,0) > 0) then 
 
	select	max(a.nr_seq_prestador_exec), 
		max(a.nr_sequencia) 
	into STRICT	nr_seq_prestador_exec_w, 
		nr_seq_conta_w 
	from	pls_conta a, 
		pls_conta_mat b 
	where	a.nr_sequencia = b.nr_seq_conta 
	and	b.nr_sequencia = nr_seq_conta_mat_p;
 
	select	max(nr_seq_prestador_pgto) 
	into STRICT	nr_seq_prestador_pgto_w 
	from	pls_conta_medica_resumo 
	where	nr_seq_conta_mat	= nr_seq_conta_mat_p 
	and 	nr_seq_conta		= nr_seq_conta_w 
	and	((ie_situacao != 'I') or (coalesce(ie_situacao::text, '') = ''));
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_item_analise_w 
	from	w_pls_resumo_conta 
	where	nr_seq_analise		= nr_seq_analise_p 
	and	nr_seq_conta_mat	= nr_seq_conta_mat_p;
	 
elsif (coalesce(nr_seq_proc_partic_p,0) > 0) then 
 
	select	max(a.nr_seq_prestador_exec), 
		max(a.nr_sequencia) 
	into STRICT	nr_seq_prestador_exec_w, 
		nr_seq_conta_w 
	from	pls_conta a, 
		pls_conta_proc b, 
		pls_proc_participante c 
	where	a.nr_sequencia		= b.nr_seq_conta 
	and	c.nr_seq_conta_proc	= b.nr_sequencia 
	and	c.nr_sequencia		= nr_seq_proc_partic_p;
 
	select	max(nr_seq_prestador_pgto) 
	into STRICT	nr_seq_prestador_pgto_w 
	from	pls_conta_medica_resumo 
	where	nr_seq_participante	= nr_seq_proc_partic_p 
	and	nr_seq_conta		= nr_seq_conta_w 
	and	ie_tipo_item		= 'R' 
	and	((ie_situacao != 'I') or (coalesce(ie_situacao::text, '') = ''));
 
	if (coalesce(nr_seq_prestador_pgto_w,0) = 0) then 
		select	max(nr_seq_prestador_pgto) 
		into STRICT	nr_seq_prestador_pgto_w 
		from	pls_proc_participante 
		where	nr_sequencia		= nr_seq_proc_partic_p;	
	end if;
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_item_analise_w 
	from	w_pls_resumo_conta 
	where	nr_seq_analise		= nr_seq_analise_p 
	and	nr_seq_partic_proc	= nr_seq_proc_partic_p;
 
end if;
 
select	max(nm_prestador) 
into STRICT	nm_prestador_pag_w 
from (SELECT	b.nr_crm||' - '||pls_obter_dados_prestador(a.nr_sequencia, 'N') nm_prestador 
	from	pls_prestador a, 
			medico b 
	where	a.nr_sequencia = coalesce(nr_seq_prestador_pgto_w, nr_seq_prestador_exec_w) 
	and		coalesce(a.cd_cgc,'X') = 'X' 
	and		a.cd_pessoa_fisica = b.cd_pessoa_fisica 
	
union
 
	SELECT	a.cd_prestador||' - '||pls_obter_dados_prestador(a.nr_sequencia, 'N') 
	from	pls_prestador a 
	where	a.nr_sequencia = coalesce(nr_seq_prestador_pgto_w, nr_seq_prestador_exec_w) 
	and		coalesce(a.cd_cgc,'X') <> 'X') alias7;
 
update	w_pls_resumo_conta 
set	nr_seq_prestador_pgto 	= coalesce(nr_seq_prestador_pgto_w, nr_seq_prestador_exec_w), 
	nm_prestador_pagamento	= coalesce(nm_prestador_pag_w,substr(pls_obter_dados_prestador(	coalesce(nr_seq_prestador_pgto_w, nr_seq_prestador_exec_w),'N'),1,255)), 
	nm_usuario		= nm_usuario_p, 
	dt_atualizacao		= clock_timestamp() 
where	nr_sequencia 		= nr_seq_item_analise_w;
 
if (coalesce(ie_comitar_p,'N') = 'S') then 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_prest_pgto ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_proc_partic_p bigint, nr_seq_analise_p bigint, ie_comitar_p text, cd_estabelecimento_p bigint, nm_usuario_p text ) FROM PUBLIC;

