CREATE OR REPLACE PROCEDURE pls_atualizar_proc_desmembrado ( nr_seq_conta_proc_p bigint, nr_seq_analise_p bigint, ie_commit_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_parecer_w			varchar(4000);
cd_codigo_w			varchar(10);
ie_tipo_w			varchar(10);
ie_situacao_w			varchar(1);
ie_status_w			varchar(1);
ie_tipo_motivo_w		varchar(1);
nr_seq_conta_proc_w		bigint;
nr_seq_analise_conta_item_w	bigint;
nr_seq_item_analise_w		bigint;
nr_seq_motivo_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_codigo,
		ie_tipo,
		ie_situacao,
		ie_status
	from	pls_analise_conta_item
	where	nr_seq_conta_proc	= nr_seq_conta_proc_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_conta_proc
	where	nr_seq_proc_princ	= nr_seq_conta_proc_p;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_analise_conta_item_w,	--Glosa ocorrÃªncia do item base
	cd_codigo_w,
	ie_tipo_w,
	ie_situacao_w,
	ie_status_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		nr_seq_conta_proc_w;	--Itens criados
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		begin
		select	nr_sequencia
		into STRICT	nr_seq_item_analise_w
		from	pls_analise_conta_item
		where	cd_codigo		= cd_codigo_w
		and	ie_tipo			= ie_tipo_w
		and	nr_seq_conta_proc	= nr_seq_conta_proc_w;
		exception
		when others then
			nr_seq_item_analise_w	:= null;
			ie_situacao_w		:= null;
			ie_status_w		:= null;
		end;

		if (coalesce(nr_seq_item_analise_w,0) > 0) then
			begin
			select	nr_seq_motivo,
				ds_parecer,
				ie_tipo_motivo
			into STRICT	nr_seq_motivo_w,
				ds_parecer_w,
				ie_tipo_motivo_w
			from	pls_analise_parecer_item
			where	nr_seq_item		= nr_seq_analise_conta_item_w;
			exception
			when others then
				nr_seq_motivo_w		:= null;
				ds_parecer_w		:= null;
				ie_tipo_motivo_w	:= null;
			end;

			/* OS 462958 - dia 29/08/2012
			if	(nvl(nr_seq_motivo_w,0) > 0) then
				insert into pls_analise_parecer_item
					(nr_sequencia,
					nr_seq_item,
					nr_seq_motivo,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_parecer,
					ie_tipo_motivo)
				values	(pls_analise_parecer_item_seq.nextval,
					nr_seq_item_analise_w,
					nr_seq_motivo_w,
					sysdate,
					nm_usuario_p,
					sysdate,
					nm_usuario_p,
					ds_parecer_w,
					ie_tipo_motivo_w);
			end if;*/
			IF (ie_situacao_w IS NOT NULL AND ie_situacao_w::text <> '') and (ie_status_w IS NOT NULL AND ie_status_w::text <> '') THEN
				update	pls_analise_conta_item
				set	ie_situacao 	= ie_situacao_w,
					ie_status	= ie_status_w
				where	nr_sequencia 	= nr_seq_item_analise_w
				and	ie_status	<> 'I';
			END IF;
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
