SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_sca_inclusao_web (nr_seq_inclusao_p bigint, nr_seq_contrato_p bigint, ds_lista_sca_p text, ie_permite_sca_p text, ie_codigo_p text, nm_usuario_p text) AS $body$
DECLARE


ds_temp_w		varchar(2);
ds_sequencia_w		varchar(10) := '';
nr_sequencia_w		bigint;
qt_tamanho_w		bigint;
qt_loop_w		bigint := 0;
nr_seq_plano_inclusao_w	pls_plano.nr_sequencia%type;

/* ie_codigo_p: 'S': CD_PLANO, 'N': NR_SEQUENCIA */

BEGIN

qt_tamanho_w := length(ds_lista_sca_p);

delete from pls_inclusao_benef_sca
where 	    nr_seq_inclusao_benef = nr_seq_inclusao_p;

select	max(nr_seq_plano)
into STRICT	nr_seq_plano_inclusao_w
from	pls_inclusao_beneficiario
where	nr_sequencia = nr_seq_inclusao_p;

if (ds_lista_sca_p IS NOT NULL AND ds_lista_sca_p::text <> '') then
	for i in 1..length(ds_lista_sca_p) loop
	begin
		qt_loop_w := qt_loop_w + 1;
		
		ds_temp_w := substr(ds_lista_sca_p, i, 1);
		
		if	((ds_temp_w = ',' or qt_loop_w = qt_tamanho_w) and length(ds_sequencia_w) > 0) then
			if	(qt_loop_w = qt_tamanho_w and ds_temp_w <> ',') then -- quando nao tem ';'
				ds_sequencia_w := ds_sequencia_w ||ds_temp_w;
			end if;
			
			if (ie_codigo_p = 'S') then
				select	max(nr_sequencia)
				into STRICT	nr_sequencia_w
				from	pls_plano
				where	cd_plano = ds_sequencia_w;
			else
				nr_sequencia_w := (ds_sequencia_w)::numeric;
			end if;
			
			if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
				insert	into pls_inclusao_benef_sca(nr_sequencia, nr_seq_inclusao_benef, dt_atualizacao,
								    nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
								    nr_seq_sca, ie_selecao, ie_lancamento_regra)
							    values (nextval('pls_inclusao_benef_sca_seq'), nr_seq_inclusao_p, clock_timestamp(),
								    nm_usuario_p, clock_timestamp(), nm_usuario_p,
								    nr_sequencia_w, 'S', 'N');
			end if;

			ds_sequencia_w := '';
		else
			ds_sequencia_w := ds_sequencia_w ||ds_temp_w;
		end if;
	end;
	end loop;
end if;

if (ie_permite_sca_p = 'S') then
	insert	into pls_inclusao_benef_sca(nr_sequencia, nr_seq_inclusao_benef, dt_atualizacao,
					    nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					    nr_seq_sca, ie_selecao, ie_lancamento_regra)
					(SELECT  nextval('pls_inclusao_benef_sca_seq'), nr_seq_inclusao_p, clock_timestamp(),
						 nm_usuario_p, clock_timestamp(), nm_usuario_p,
						 a.nr_seq_plano, coalesce(a.ie_lancar_sca_benef,'N'), 'S'
					 from	 pls_sca_regra_contrato a
					 where   a.nr_seq_contrato = nr_seq_contrato_p
					 and	 ((coalesce(a.nr_seq_plano_benef::text, '') = '') or (a.nr_seq_plano_benef = nr_seq_plano_inclusao_w))
					 and	 trunc(clock_timestamp()) between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and trunc(coalesce(a.dt_fim_vigencia, clock_timestamp()))
					 and	 not exists (select  	1
							     from	pls_inclusao_benef_sca b
							     where	b.nr_seq_inclusao_benef = nr_seq_inclusao_p
							     and	b.nr_seq_sca = a.nr_seq_plano));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_sca_inclusao_web (nr_seq_inclusao_p bigint, nr_seq_contrato_p bigint, ds_lista_sca_p text, ie_permite_sca_p text, ie_codigo_p text, nm_usuario_p text) FROM PUBLIC;

