CREATE OR REPLACE PROCEDURE pls_atualizar_scpa_plano () AS $body$
DECLARE


ds_erro_w		varchar(255);


BEGIN

begin
update	pls_plano
set	cd_scpa	= cd_codigo_ant
where	coalesce(cd_codigo_ant,'0') <> '0'
and	coalesce(cd_scpa,'0')	<> coalesce(cd_codigo_ant,'0');

update	pls_plano
set	cd_codigo_ant  = NULL
where	coalesce(cd_codigo_ant,'0') <> '0';
exception
when others then
	ds_erro_w	:= 'Erros durante o processo de atualização';
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
