CREATE OR REPLACE PROCEDURE pls_atualizar_status_benef_web ( nr_sequencia_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* IE_OPCAO_P
	1 - Ativo
	2 - Inativo
*/
ie_situacao_w			varchar(1);


BEGIN

if (ie_opcao_p	= 1) then
	ie_situacao_w	:= 'A';
elsif (ie_opcao_p	= 2) then
	ie_situacao_w	:= 'I';
end if;

update	pls_segurado_web
set	ie_situacao	= ie_situacao_w,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
