SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_status_contas ( ds_lista_sequencias_p text, ie_acao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_sequencias_w	varchar(2000);
ie_status_w		varchar(2);
nr_pos_virgula_w	bigint;
nr_seq_sequencia_w	bigint;

/* 
Ações 
C = Consistir conta. 
DC = desfazer consistir conta 
F = Fechar conta 
DF = desfazer fechamento conta 
*/
 
 
 

BEGIN 
if (ds_lista_sequencias_p IS NOT NULL AND ds_lista_sequencias_p::text <> '') and (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_lista_sequencias_w	:= ds_lista_sequencias_p;
	 
	while(ds_lista_sequencias_w IS NOT NULL AND ds_lista_sequencias_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_sequencias_w);
	 
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_seq_sequencia_w	:= (substr(ds_lista_sequencias_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_sequencias_w	:= substr(ds_lista_sequencias_w,nr_pos_virgula_w+1,length(ds_lista_sequencias_w));			
			end;
		else 
			begin 
			nr_seq_sequencia_w	:= (ds_lista_sequencias_w)::numeric;
			ds_lista_sequencias_w	:= null;
			end;
		end if;
			 
		if (nr_seq_sequencia_w > 0) then 
			begin 
 
			select	ie_status 
			into STRICT	ie_status_w 
			from	pls_conta 
			where	nr_sequencia = nr_seq_sequencia_w;
			 
 
			if (ie_acao_p = 'C') and (ie_status_w = 'U') then 
				begin 
				CALL pls_consistir_conta( 
					nr_seq_sequencia_w, 
					cd_estabelecimento_p, 
					nm_usuario_p,'N',null,null,null,'S');
				end;
			elsif (ie_acao_p = 'DC') and (ie_status_w = 'P' or ie_status_w = 'L') then 
				begin 
				CALL pls_desfazer_consistir_conta( 
					nr_seq_sequencia_w, 
					cd_estabelecimento_p, 
					nm_usuario_p);
				end;
			elsif (ie_acao_p = 'F') and (ie_status_w = 'L') then 
				begin 
				CALL pls_fechar_conta( 
					nr_seq_sequencia_w, 
					'S', 
					'S', 
					'S', 
					cd_estabelecimento_p, 
					nm_usuario_p, 
					null, 
					null);
				end;
			elsif (ie_acao_p = 'DF') and (ie_status_w = 'F') then 
				begin 
				CALL pls_desfazer_fechamento_conta( 
					nr_seq_sequencia_w, 
					cd_estabelecimento_p, 
					nm_usuario_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;	
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
