SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_status_fat ( nr_seq_analise_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nr_seq_item_pos_w		bigint;
qt_ocorrencia_pos_w		bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_item_resumo_pos_w 	bigint;
ie_status_w			varchar(1);
				
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_conta_proc, 
		nr_seq_conta_mat 
	from	pls_conta_pos_estabelecido 
	where	nr_seq_analise = nr_seq_analise_p 
	and	((ie_situacao		= 'A') or (coalesce(ie_situacao::text, '') = '')) 
	order by 1;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_item_pos_w, 
	nr_seq_conta_proc_w, 
	nr_seq_conta_mat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	max(ie_status)	 
	into STRICT	ie_status_w 
	from	w_pls_resumo_conta 
	where	((nr_seq_conta_proc = nr_seq_conta_proc_w) or (nr_seq_conta_mat = nr_seq_conta_mat_w)) 
	and	nr_seq_analise = nr_seq_analise_p;
	 
	/*	D        Pendente de análise 
		L        Liberado para faturamento 
		N        Não permite faturamento 
		P        Permitido faturamento 
		U        Usuário (Aguardando Ação)	*/
 
	 
	if (ie_status_w = 'P') then 
	 
		select	count(a.nr_sequencia) 
		into STRICT	qt_ocorrencia_pos_w 
		from	pls_ocorrencia_benef a, 
			pls_analise_conta_item b 
		where	a.nr_sequencia		 = b.nr_seq_ocorrencia 
		and	a.nr_seq_conta_pos_estab = nr_seq_item_pos_w 
		and	b.ie_status <> 'I';
		 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_item_resumo_pos_w 
		from	w_pls_resumo_conta 
		where	((nr_seq_conta_proc = nr_seq_conta_proc_w) or (nr_seq_conta_mat = nr_seq_conta_mat_w)) 
		and	nr_seq_analise = nr_seq_analise_p;
		 
		if (coalesce(nr_seq_item_resumo_pos_w,0) > 0) then					 
			 
			if (qt_ocorrencia_pos_w > 0) then			 
				update	w_pls_resumo_conta 
				set	ie_status = 'D' 
				where	nr_sequencia = nr_seq_item_resumo_pos_w;			
			else 
				/*Não havendo ocorrências e o mesmo estiver liberado já é possui liberar definitivo. */
 
				update	w_pls_resumo_conta 
				set	ie_status 	= 'L' 
				where	nr_sequencia 	= nr_seq_item_resumo_pos_w;
			end if;		
		end if;
	end if;
	 
	end;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_status_fat ( nr_seq_analise_p bigint, nm_usuario_p text ) FROM PUBLIC;

