SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_status_item_aud ( nr_seq_item_p bigint, nr_seq_grupo_p bigint, ie_acao_p text, ie_liberacao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_ação_p
  L - Liberado
  N - Negado
  P - Pendente

ie_liberacao_p
 A - Análise
 I - Item da análise
*/
nr_nivel_auditor_w		bigint;
nr_seq_aud_ocor_glosa_aut_w	bigint;
nr_nivel_liberacao_w		bigint;
ie_status_w			varchar(2);
ie_status_analise_w		varchar(2);
qt_itens_negado_w		bigint;
qt_itens_pendente_w		bigint;
ie_permissao_w			varchar(1);
qt_itens_total_w		bigint;
nr_seq_ocorrencia_w		bigint;
ie_auditor_pertence_ocorr_w	varchar(1);
ie_tipo_auditoria_w		smallint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_nivel_liberacao,
		ie_status,
		nr_seq_ocorrencia
	from	pls_analise_ocor_glosa_aut
	where	nr_seq_aud_item = nr_seq_item_p;



BEGIN

begin
	select	ie_permissao,
		ie_tipo_auditoria
	into STRICT	ie_permissao_w,
		ie_tipo_auditoria_w
	from	pls_grupo_auditor
	where	nr_sequencia = nr_seq_grupo_p
	and	ie_situacao = 'A';
exception
when others then
	ie_permissao_w := null;
end;

if (ie_permissao_w = 'T') then
	begin
		select	max(nr_nivel_liberacao)
		into STRICT	nr_nivel_auditor_w
		from	pls_nivel_liberacao;
	exception
	when others then
		nr_nivel_auditor_w := 0;
	end;
else
	nr_nivel_auditor_w	:= coalesce((pls_obter_dados_auditor(null,null,nm_usuario_p,'L',nr_seq_grupo_p))::numeric ,0);
end if;

if (ie_liberacao_p = 'I') then

	select	count(1)
	into STRICT	qt_itens_total_w
	from	pls_analise_ocor_glosa_aut
	where	nr_seq_aud_item = nr_seq_item_p;

	if (qt_itens_total_w > 0) and (ie_tipo_auditoria_w = 1) then
		open C01;
		loop
		fetch C01 into
			nr_seq_aud_ocor_glosa_aut_w,
			nr_nivel_liberacao_w,
			ie_status_w,
			nr_seq_ocorrencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			nr_seq_ocorrencia_w := coalesce(nr_seq_ocorrencia_w,0);

			ie_auditor_pertence_ocorr_w := pls_obter_grupo_pertence_ocorr(nr_seq_ocorrencia_w,nr_seq_grupo_p);

			if (ie_auditor_pertence_ocorr_w = 'S') and (ie_status_w <> ie_acao_p) and (nr_nivel_auditor_w >= nr_nivel_liberacao_w) then
				update 	pls_analise_ocor_glosa_aut
				set	ie_status	= ie_acao_p,
					dt_atualizacao	= clock_timestamp(),
					nm_usuario	= nm_usuario_p
				where	nr_sequencia = nr_seq_aud_ocor_glosa_aut_w;
			end if;
			end;
		end loop;
		close C01;

		select	count(1)
		into STRICT	qt_itens_negado_w
		from	pls_analise_ocor_glosa_aut
		where	nr_seq_aud_item = nr_seq_item_p
		and	ie_status = 'N';

		if (qt_itens_negado_w > 0) then
			ie_status_analise_w  := 'N';
		else
			select	count(1)
			into STRICT	qt_itens_pendente_w
			from	pls_analise_ocor_glosa_aut
			where	nr_seq_aud_item = nr_seq_item_p
			and	ie_status = 'P';

			if (qt_itens_pendente_w > 0) then
				ie_status_analise_w  := 'P';
			else
				ie_status_analise_w  := 'A';
			end if;
		end if;

	else
		select	CASE WHEN ie_acao_p='L' THEN 'A'  ELSE ie_acao_p END
		into STRICT	ie_status_analise_w
		;

		if (ie_tipo_auditoria_w in (2,3)) then
			update 	pls_analise_ocor_glosa_aut
			set	ie_status	= ie_acao_p,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p
			where	nr_seq_aud_item = nr_seq_item_p;
		end if;

	end if;

	update	pls_auditoria_item
	set	ie_status	= ie_status_analise_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_item_p;

end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualizar_status_item_aud ( nr_seq_item_p bigint, nr_seq_grupo_p bigint, ie_acao_p text, ie_liberacao_p text, nm_usuario_p text) FROM PUBLIC;

