CREATE OR REPLACE PROCEDURE pls_atualizar_status_ocor_comb ( nr_seq_ocorrencia_p bigint, nr_seq_ocor_combinada_p bigint, nr_seq_motivo_glosa_p bigint, nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nr_seq_execucao_p bigint, nr_seq_guia_proc_p bigint, nr_seq_guia_mat_p bigint, nr_seq_req_proc_p bigint, nr_seq_req_mat_p bigint, nr_seq_exec_proc_p bigint, nr_seq_exec_mat_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar os itens da guia conforme a geração de ocorr~encia combinada.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_auditoria_w        varchar(2);
ie_glosa_w        varchar(2);
ie_define_se_gera_analise_w    varchar(255);
ie_tipo_item_w        bigint;
nr_seq_item_w        bigint;


BEGIN

if (nr_seq_guia_proc_p IS NOT NULL AND nr_seq_guia_proc_p::text <> '') then
  nr_seq_item_w  := nr_seq_guia_proc_p;
  ie_tipo_item_w  := 1;
elsif (nr_seq_guia_mat_p IS NOT NULL AND nr_seq_guia_mat_p::text <> '') then
  nr_seq_item_w  := nr_seq_guia_mat_p;
  ie_tipo_item_w  := 2;
elsif (nr_seq_req_proc_p IS NOT NULL AND nr_seq_req_proc_p::text <> '') then
  nr_seq_item_w  := nr_seq_req_proc_p;
  ie_tipo_item_w  := 5;
elsif (nr_seq_req_mat_p IS NOT NULL AND nr_seq_req_mat_p::text <> '') then
  nr_seq_item_w  := nr_seq_req_mat_p;
  ie_tipo_item_w  := 6;
elsif (nr_seq_exec_proc_p IS NOT NULL AND nr_seq_exec_proc_p::text <> '') then
  nr_seq_item_w  := nr_seq_exec_proc_p;
  ie_tipo_item_w  := 10;
elsif (nr_seq_exec_mat_p IS NOT NULL AND nr_seq_exec_mat_p::text <> '') then
  nr_seq_item_w  := nr_seq_exec_mat_p;
  ie_tipo_item_w  := 11;
end if;

select  ie_auditoria
into STRICT  ie_auditoria_w
from  pls_ocorrencia
where  nr_sequencia  = nr_seq_ocorrencia_p
and  ie_situacao  = 'A';

if (ie_tipo_item_w  in (1,2)) then
  ie_define_se_gera_analise_w := pls_obter_define_gera_analise(cd_estabelecimento_p,'A');
elsif (ie_tipo_item_w  in (5,6)) then
  ie_define_se_gera_analise_w := pls_obter_define_gera_analise(cd_estabelecimento_p,'R');
end if;

if (ie_auditoria_w = 'N') and (ie_tipo_item_w in (1,2,5,6)) then
  ie_auditoria_w := pls_obter_se_item_glosa_ocorr(nr_seq_guia_p, nr_seq_requisicao_p, nr_seq_item_w, ie_tipo_item_w);
end if;

if (ie_auditoria_w = 'N') then
  ie_glosa_w := pls_obter_se_item_glosa(nr_seq_item_w,ie_tipo_item_w);
else
  --Se a veriavel "ie_define_se_gera_analise_w", estiver com o valor "N", então irá verificar se para o item existe glosa definitiva, e se existir  então irá negar o item
  if (ie_define_se_gera_analise_w = 'N') and (ie_tipo_item_w in (1,2,5,6)) then
    ie_auditoria_w := pls_obter_se_gerar_analise(coalesce(nr_seq_guia_p,nr_seq_requisicao_p),  nr_seq_item_w, ie_tipo_item_w);

    select  CASE WHEN ie_auditoria_w='N' THEN 'S'  ELSE 'N' END
    into STRICT  ie_glosa_w
;
  end if;
end if;

if (ie_auditoria_w  = 'N') then
  if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
    ie_auditoria_w  := coalesce(pls_obter_acao_ocor_comb_guia(nr_seq_guia_p, 'O'),'N');
  elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
    ie_auditoria_w  := coalesce(pls_obter_acao_ocor_comb_req(nr_seq_requisicao_p,nr_seq_req_proc_p,nr_seq_req_mat_p, 'O'),'N');
  end if;
end if;

if (ie_auditoria_w  = 'S') then
  if (ie_tipo_item_w  = 1) then
    update  pls_guia_plano_proc
    set  ie_status  = 'A',
      qt_autorizada  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_guia_proc_p;
  elsif (ie_tipo_item_w  = 2) then
    update  pls_guia_plano_mat
    set  ie_status  = 'A',
      qt_autorizada  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_guia_mat_p;
  elsif (ie_tipo_item_w  = 5) then
    update  pls_requisicao_proc
    set  ie_status  = 'A',
      qt_procedimento  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_req_proc_p;
  elsif (ie_tipo_item_w  = 6) then
    update  pls_requisicao_mat
    set  ie_status  = 'A',
      qt_material  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_req_mat_p;
  elsif (ie_tipo_item_w  = 10) then
    update  pls_execucao_req_item
    set  ie_situacao  = 'A',
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_exec_proc_p;
  elsif (ie_tipo_item_w  = 11) then
    update  pls_execucao_req_item
    set  ie_situacao  = 'A',
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_exec_mat_p;
  end if;
elsif (ie_auditoria_w  = 'N') and (ie_glosa_w    = 'S') then
  if (ie_tipo_item_w  = 1) then
    update  pls_guia_plano_proc
    set  ie_status  = 'N',
      qt_autorizada  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_guia_proc_p;
  elsif (ie_tipo_item_w  = 2) then
    update  pls_guia_plano_mat
    set  ie_status  = 'N',
      qt_autorizada  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_guia_mat_p;
  elsif (ie_tipo_item_w  = 5) then
    update  pls_requisicao_proc
    set  ie_status  = 'N',
      qt_procedimento  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_req_proc_p;
  elsif (ie_tipo_item_w  = 6) then
    update  pls_requisicao_mat
    set  ie_status  = 'N',
      qt_material  = 0,
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_req_mat_p;
  elsif (ie_tipo_item_w  = 10) then
    update  pls_execucao_req_item
    set  ie_situacao  = 'N',
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_exec_proc_p;
  elsif (ie_tipo_item_w  = 11) then
    update  pls_execucao_req_item
    set  ie_situacao  = 'N',
      dt_atualizacao  = clock_timestamp(),
      nm_usuario  = nm_usuario_p
    where  nr_sequencia  = nr_seq_exec_mat_p;
  end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
