CREATE OR REPLACE PROCEDURE pls_atualizar_stat_lote_canc ( nr_seq_lote_p bigint, ie_status_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar o status do lote de cancelamento de receita.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: passar o ie_status_p corretamente

ie_opcao_p:
'M' - Mensalidade
'F' - Faturamento
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (ie_status_p IS NOT NULL AND ie_status_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	if (ie_opcao_p = 'M') then
		update  pls_cancel_rec_mens_lote
		set     ie_status	= ie_status_p,
			dt_consistencia = CASE WHEN ie_status_p='P' THEN  null  ELSE dt_consistencia END
		where   nr_sequencia	= nr_seq_lote_p;

		-- uso aqui o ie_status_p porque tem o mesmo significado da tabela pls_cancel_rec_mens_lote
		update  pls_cancel_rec_mensalidade
		set     ie_status	= ie_status_p
		where   nr_seq_lote	= nr_seq_lote_p;
	elsif (ie_opcao_p = 'F') then
		update  pls_cancel_rec_fat_lote
		set     ie_status	= ie_status_p,
			dt_consistencia = CASE WHEN ie_status_p='P' THEN  null  ELSE dt_consistencia END
		where   nr_sequencia	= nr_seq_lote_p;

		-- uso aqui o ie_status_p porque tem o mesmo significado da tabela pls_cancel_rec_fat_lote
		update  pls_cancel_rec_fatura
		set     ie_status	= ie_status_p
		where   nr_seq_lote	= nr_seq_lote_p;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
