SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualizar_valor_digitacao ( ie_tipo_despesa_p text, nr_seq_conta_proc_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
cd_procedimento_w	bigint;
ie_origem_proced_w	integer;
ie_tipo_despesa_w	varchar(3);
nr_seq_pacote_w		bigint;
ie_calculo_pacote_w	varchar(2):= 'P';

 

BEGIN 
 
select	cd_procedimento, 
		ie_origem_proced 
into STRICT	cd_procedimento_w, 
		ie_origem_proced_w 
from	pls_conta_proc 
where	nr_sequencia = nr_seq_conta_proc_p;
 
select	max(nr_sequencia) 
into STRICT	nr_seq_pacote_w 
from	pls_pacote 
where	cd_procedimento	= cd_procedimento_w 
and	ie_origem_proced	= ie_origem_proced_w 
and	ie_situacao			= 'A';
 
if (coalesce(nr_seq_pacote_w,0) > 0 ) then 
	ie_tipo_despesa_w := 4;
	select	coalesce(max(ie_calculo_pacote),'P') 
	into STRICT	ie_calculo_pacote_w 
	from	pls_parametros 
	where	cd_estabelecimento	= cd_estabelecimento_p;
else 
	select 	substr(coalesce(max(ie_classificacao),'1'),1,1) 
	into STRICT	ie_tipo_despesa_w 
	from	procedimento 
	where	cd_procedimento	= cd_procedimento_w 
	and	ie_origem_proced	= ie_origem_proced_w;
	 
	ie_tipo_despesa_w	:= substr(ie_tipo_despesa_w,1,1);
end if;
if (ie_tipo_despesa_w = '1') then /* Atualizar o valor do procedimento */
 
	CALL pls_atualiza_valor_proc(nr_seq_conta_proc_p, 'N', nm_usuario_p,'S',null,null);
elsif (ie_tipo_despesa_w in ('2','3')) then /* Atualizar os valores das taxas e diárias */
 
	CALL pls_atualiza_valor_servico(nr_seq_conta_proc_p, 'N', nm_usuario_p,'S');
elsif (ie_tipo_despesa_w = '4') then /* Atualizar os valores dos pacotes */
 
	if (ie_calculo_pacote_w	= 'R') then 
		CALL pls_atualiza_valor_proc(nr_seq_conta_proc_p, 'N', nm_usuario_p,'S',null,null);
	else 
		CALL pls_atualiza_valor_pacote(nr_seq_conta_proc_p, 'C', nm_usuario_p, 'S', 'N');
	end if;
else 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(191853);
	--(-20011,'Problemas com a geração de valores. Tipo de despesa do procedimento inválido!#@#@'); 
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
