CREATE OR REPLACE PROCEDURE pls_atualizar_valor_lote_pp ( nr_seq_pagamento_prest_p pls_pp_prestador.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

					
nr_seq_lote_w		pls_pp_prestador.nr_seq_lote%type;
nr_seq_prestador_w	pls_pp_prestador.nr_seq_prestador%type;
vl_item_w		pls_pp_item_lote.vl_item%type;
vl_liquido_w		pls_pp_item_lote.vl_liquido%type;
vl_glosa_w		pls_pp_item_lote.vl_glosa%type;

C01 CURSOR(	nr_seq_lote_pc		pls_pp_prestador.nr_seq_lote%type,
		nr_seq_prestador_pc	pls_pp_prestador.nr_seq_prestador%type)FOR
	SELECT	nr_sequencia nr_seq_pp_prest_evento_valor,
		nr_seq_lote,
		nr_seq_prestador,
		nr_seq_evento
	from	pls_pp_prest_evento_valor
	where	nr_seq_lote = nr_seq_lote_pc
	and	nr_seq_prestador = nr_seq_prestador_pc;
	
BEGIN

if (nr_seq_pagamento_prest_p IS NOT NULL AND nr_seq_pagamento_prest_p::text <> '') then
	select	max(nr_seq_lote),
		max(nr_seq_prestador)
	into STRICT	nr_seq_lote_w,
		nr_seq_prestador_w
	from	pls_pp_prestador
	where	nr_sequencia = nr_seq_pagamento_prest_p;
	
	for r_c01_w in c01(nr_seq_lote_w, nr_seq_prestador_w) loop
		select	coalesce(sum(vl_item),0),
			coalesce(sum(vl_liquido),0),
			coalesce(sum(vl_glosa),0)
		into STRICT	vl_item_w,
			vl_liquido_w,
			vl_glosa_w
		from	pls_pp_item_lote
		where	nr_seq_lote		= r_c01_w.nr_seq_lote
		and	nr_seq_prestador	= r_c01_w.nr_seq_prestador
		and	nr_seq_evento		= r_c01_w.nr_seq_evento;
		
		update	pls_pp_prest_evento_valor
		set	vl_item		= vl_item_w,
			vl_liquido	= vl_liquido_w,
			vl_glosa	= vl_glosa_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia 	= r_c01_w.nr_seq_pp_prest_evento_valor;
	end loop;
	
	select	coalesce(sum(vl_liquido),0),
		coalesce(sum(vl_glosa),0)
	into STRICT	vl_liquido_w,
		vl_glosa_w
	from	pls_pp_prest_evento_valor
	where	nr_seq_lote 		= nr_seq_lote_w
	and	nr_seq_prestador 	= nr_seq_prestador_w;
	
	update	pls_pp_prestador
	set	vl_liquido	= vl_liquido_w,
		vl_provento	= vl_liquido_w,
		vl_titulo_pagar	= vl_liquido_w,
		vl_glosa	= vl_glosa_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_pagamento_prest_p;
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
