CREATE OR REPLACE PROCEDURE pls_atualizar_valor_recurso ( nr_seq_item_rec_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/* 	C = Conta
	P = Protocolo */
vl_recursado_proc_rec_w		pls_rec_glosa_proc.vl_recursado%type;
vl_acatado_proc_rec_w		pls_rec_glosa_proc.vl_acatado%type;
vl_recursado_mat_rec_w		pls_rec_glosa_mat.vl_recursado%type;
vl_acatado_mat_rec_w		pls_rec_glosa_mat.vl_acatado%type;
vl_recursado_conta_w		pls_rec_glosa_conta.vl_total_recursado%type;
vl_acatado_conta_w		pls_rec_glosa_conta.vl_total_acatado%type;


BEGIN

if (ie_opcao_p = 'C') then
	select	sum(vl_recursado),
		sum(vl_acatado)
	into STRICT	vl_recursado_proc_rec_w,
		vl_acatado_proc_rec_w
	from	pls_rec_glosa_proc
	where	nr_seq_conta_rec = nr_seq_item_rec_p;

	select	sum(vl_recursado),
		sum(vl_acatado)
	into STRICT	vl_recursado_mat_rec_w,
		vl_acatado_mat_rec_w
	from	pls_rec_glosa_mat
	where	nr_seq_conta_rec = nr_seq_item_rec_p;

	vl_recursado_conta_w	:= coalesce(vl_recursado_proc_rec_w,0) + coalesce(vl_recursado_mat_rec_w,0);
	vl_acatado_conta_w	:= coalesce(vl_acatado_proc_rec_w,0) + coalesce(vl_acatado_mat_rec_w,0);

	if (vl_recursado_conta_w > 0) or (vl_acatado_conta_w > 0) then
		update	pls_rec_glosa_conta
		set	vl_total_recursado	= vl_recursado_conta_w,
			vl_total_acatado	= vl_acatado_conta_w
		where	nr_sequencia		= nr_seq_item_rec_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
