CREATE OR REPLACE PROCEDURE pls_atualiza_atributo_ptu_ir ( nr_seq_pls_fatura_p pls_fatura.nr_sequencia%type, ie_verif_existe_ptu_p text, ie_commit_p text) AS $body$
DECLARE

/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
OPS - Faturamento
Finalidade: Atualizar o IR da fatura PTU
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/nr_seq_fatura_pls_w	pls_fatura.nr_sequencia%type;

qt_fatura_ptu_w		integer := 0;
vl_ir_w			pls_fatura_trib.vl_tributo%type := 0;



BEGIN

if (nr_seq_pls_fatura_p IS NOT NULL AND nr_seq_pls_fatura_p::text <> '') then

	nr_seq_fatura_pls_w	:= nr_seq_pls_fatura_p;

	if (ie_verif_existe_ptu_p = 'S') then

		select	count(1)
		into STRICT	qt_fatura_ptu_w
		from	ptu_fatura	a
		where	a.nr_seq_pls_fatura	= nr_seq_fatura_pls_w;
	end if;

	if	((ie_verif_existe_ptu_p = 'N') or (qt_fatura_ptu_w > 0)) then

		begin
			select	coalesce(sum(coalesce(k.vl_tributo,0)),0)
			into STRICT	vl_ir_w
			from	tributo			y,
				pls_fatura_trib		k
			where	k.cd_tributo		= y.cd_tributo
			and	y.ie_tipo_tributo	= 'IR'
			and	k.nr_seq_fatura		= nr_seq_fatura_pls_w;
		exception
			when others then
				vl_ir_w	:= 0;
		end;

		update	ptu_fatura
		set	vl_ir	= vl_ir_w
		where	nr_seq_pls_fatura	= nr_seq_fatura_pls_w;

		if (ie_commit_p = 'S') then
			commit;
		end if;

	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
