SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_copar_contab_coop () AS $body$
DECLARE

 
nr_seq_copart_contab_w		pls_util_cta_pck.t_number_table;
vl_ato_cooperado_w		pls_util_cta_pck.t_number_table;
vl_ato_auxiliar_w		pls_util_cta_pck.t_number_table;
vl_ato_nao_cooperado_w		pls_util_cta_pck.t_number_table;
nr_seq_prestador_pgto_w		pls_util_cta_pck.t_number_table;
ie_ato_cooperado_w		pls_util_cta_pck.t_varchar2_table_10;
index_w				integer := 0;

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.nr_seq_conta_resumo, 
		a.nr_seq_conta, 
		b.nr_seq_conta_proc, 
		b.nr_seq_conta_mat, 
		a.vl_coparticipacao 
	from	pls_conta_copartic_contab	a, 
		pls_conta_coparticipacao	b 
	where	a.nr_seq_conta_copartic		= b.nr_sequencia 
	and	coalesce(a.nr_seq_prestador_pgto::text, '') = ''; --Colocada essa restrição pois foi o último campo a ser criado na tabela 
BEGIN 
 
for r_c01_w in C01 loop 
 
	nr_seq_copart_contab_w(index_w)		:= r_c01_w.nr_sequencia;
	ie_ato_cooperado_w(index_w)		:= '';
	vl_ato_cooperado_w(index_w)		:= 0;
	vl_ato_auxiliar_w(index_w)		:= 0;
	vl_ato_nao_cooperado_w(index_w)		:= 0;
	nr_seq_prestador_pgto_w(index_w)	:= null;
 
	if (r_c01_w.nr_seq_conta_resumo IS NOT NULL AND r_c01_w.nr_seq_conta_resumo::text <> '') then 
		select	max(ie_ato_cooperado), 
			max(nr_seq_prestador_pgto) 
		into STRICT	ie_ato_cooperado_w(index_w), 
			nr_seq_prestador_pgto_w(index_w) 
		from	pls_conta_medica_resumo 
		where	nr_sequencia 	= r_c01_w.nr_seq_conta_resumo 
		and	nr_seq_conta	= r_c01_w.nr_seq_conta;
	elsif (r_c01_w.nr_seq_conta_proc IS NOT NULL AND r_c01_w.nr_seq_conta_proc::text <> '') then 
		select	max(ie_ato_cooperado) 
		into STRICT	ie_ato_cooperado_w(index_w) 
		from	pls_conta_proc 
		where	nr_sequencia = r_c01_w.nr_seq_conta_proc;
	elsif (r_c01_w.nr_seq_conta_mat IS NOT NULL AND r_c01_w.nr_seq_conta_mat::text <> '') then 
		select	max(ie_ato_cooperado) 
		into STRICT	ie_ato_cooperado_w(index_w) 
		from	pls_conta_mat 
		where	nr_sequencia = r_c01_w.nr_seq_conta_mat;
	end if;
 
	if (ie_ato_cooperado_w(index_w) = '1') then 
		vl_ato_cooperado_w(index_w)	:= r_c01_w.vl_coparticipacao;
	elsif (ie_ato_cooperado_w(index_w) = '2') then 
		vl_ato_auxiliar_w(index_w)	:= r_c01_w.vl_coparticipacao;
	elsif (ie_ato_cooperado_w(index_w) = '3') then 
		vl_ato_nao_cooperado_w(index_w)	:= r_c01_w.vl_coparticipacao;
	end if;
	 
	if (mod(index_w,1500) = 0) then 
		forall i in nr_seq_copart_contab_w.first..nr_seq_copart_contab_w.last	 
			update	pls_conta_copartic_contab 
			set	ie_ato_cooperado	= ie_ato_cooperado_w(i), 
				vl_ato_cooperado	= vl_ato_cooperado_w(i), 
				vl_ato_auxiliar		= vl_ato_auxiliar_w(i), 
				vl_ato_nao_cooperado	= vl_ato_nao_cooperado_w(i), 
				nr_seq_prestador_pgto	= nr_seq_prestador_pgto_w(i) 
			where	nr_sequencia		= nr_seq_copart_contab_w(i);
			 
		commit;
		nr_seq_copart_contab_w.delete;
		ie_ato_cooperado_w.delete;
		vl_ato_cooperado_w.delete;
		vl_ato_auxiliar_w.delete;
		vl_ato_nao_cooperado_w.delete;
		nr_seq_prestador_pgto_w.delete;
		index_w	:= 0;
	 
	else 
		index_w	:= index_w + 1;
	end if;
end loop;
 
if (nr_seq_copart_contab_w.count > 0) then 
	forall i in nr_seq_copart_contab_w.first..nr_seq_copart_contab_w.last	 
		update	pls_conta_copartic_contab 
		set	ie_ato_cooperado	= ie_ato_cooperado_w(i), 
			vl_ato_cooperado	= vl_ato_cooperado_w(i), 
			vl_ato_auxiliar		= vl_ato_auxiliar_w(i), 
			vl_ato_nao_cooperado	= vl_ato_nao_cooperado_w(i), 
			nr_seq_prestador_pgto	= nr_seq_prestador_pgto_w(i) 
		where	nr_sequencia		= nr_seq_copart_contab_w(i);
		 
		commit;
		nr_seq_copart_contab_w.delete;
		ie_ato_cooperado_w.delete;
		vl_ato_cooperado_w.delete;
		vl_ato_auxiliar_w.delete;
		vl_ato_nao_cooperado_w.delete;
		nr_seq_prestador_pgto_w.delete;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualiza_copar_contab_coop () FROM PUBLIC;

