SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_dados_contrato ( nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_congenere_w		bigint;
ie_tipo_beneficiario_w		varchar(3);
cd_cooperativa_w		varchar(10);
cd_classif_contrato_w		varchar(14);
nr_seq_classificacao_w		bigint;


BEGIN

select	coalesce(max(cd_cooperativa),0)
into STRICT	cd_cooperativa_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

/*aaschlote 21/08/2010 - OS 242380*/

if (cd_cooperativa_w > 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_congenere_w
	from	pls_congenere
	where	cd_cooperativa	= cd_cooperativa_w;

	update	pls_contrato
	set	nr_seq_congenere	= nr_seq_congenere_w
	where	nr_sequencia		= nr_seq_contrato_p;
else
	update	pls_contrato
	set	nr_seq_congenere	 = NULL
	where	nr_sequencia		= nr_seq_contrato_p;
end if;

select	coalesce(max(nr_seq_congenere),0)
into STRICT	nr_seq_congenere_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (nr_seq_congenere_w	> 0) then
	ie_tipo_beneficiario_w	:= 'ENB';
elsif (nr_seq_congenere_w = 0) then
	ie_tipo_beneficiario_w	:= 'BE';
end if;

update	pls_contrato
set	ie_tipo_beneficiario	= ie_tipo_beneficiario_w
where	nr_sequencia	= nr_seq_contrato_p;

select	max(cd_classif_contrato)
into STRICT	cd_classif_contrato_w
from	pls_contrato
where	nr_sequencia = nr_seq_contrato_p;

if (cd_classif_contrato_w IS NOT NULL AND cd_classif_contrato_w::text <> '') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_classificacao_w
	from	pls_classificacao_contrato
	where	cd_classificacao = cd_classif_contrato_w;

	update	pls_contrato
	set	nr_seq_classificacao	= nr_seq_classificacao_w
	where	nr_sequencia		= nr_seq_contrato_p;
	end;
else
	update	pls_contrato
	set	nr_seq_classificacao	 = NULL
	where	nr_sequencia		= nr_seq_contrato_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
