CREATE OR REPLACE PROCEDURE pls_atualiza_dados_gerac_a600 (nr_seq_fatura_p ptu_camara_compensacao.nr_sequencia%type, cd_unimed_origem_p ptu_camara_compensacao.cd_unimed_origem%type, nr_seq_arquivo_p ptu_camara_compensacao.nr_seq_arquivo%type, nm_usuario_p text) AS $body$
DECLARE


nr_seq_arquivo_w	ptu_camara_compensacao.nr_seq_arquivo%type;


BEGIN

-- Se não veio nr_seq_arquivo_p é necessário buscar o ponta a realizar a incrementação, senão, se vir valor no campo nr_seq_arquivo_p, é porque se trata de uma re-geração de arquivo, portanto utiliza-rá a mesma nr_seq_arquivo_p
if (coalesce(nr_seq_arquivo_p::text, '') = '') then
	select	coalesce(max(nr_seq_arquivo),0) + 1
	into STRICT	nr_seq_arquivo_w
	from	ptu_camara_compensacao
	where	trunc(dt_geracao_arquivo) = trunc(clock_timestamp())
	and	cd_unimed_origem = cd_unimed_origem_p
	and	nr_sequencia <> nr_seq_fatura_p;
end if;

-- Se o campo nr_seq_arquivo_w está nulo utiliza o nr_seq_arquivo_p vindo por parâmetro visto que se trata de uma re-geração de arquivo
nr_seq_arquivo_w := coalesce(nr_seq_arquivo_w, nr_seq_arquivo_p);

update	ptu_camara_compensacao
set	dt_geracao_arquivo = clock_timestamp(),
	nr_seq_arquivo = nr_seq_arquivo_w,
	nm_usuario_arq = nm_usuario_p,
	ds_arquivo = 'C' || to_char(clock_timestamp(),'ddmmyy') || nr_seq_arquivo_w || '.' || substr(lpad(somente_numero(cd_unimed_origem),3,'0'),1,3)
where	nr_sequencia = nr_seq_fatura_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
