SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_estagio_guia_conv ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(2);
nr_seq_estagio_w		bigint;
nr_sequencia_autor_w		bigint;
cd_guia_w			varchar(20);
cd_senha_w			varchar(20);


BEGIN

begin
	select	nr_sequencia
	into STRICT	nr_sequencia_autor_w
	from	autorizacao_convenio
	where	nr_seq_guia_plano = nr_seq_guia_p;
exception
when others then
	nr_sequencia_autor_w := 0;
end;

if (coalesce(nr_sequencia_autor_w,0) > 0) then

	select	max(ie_status),
		max(cd_guia),
		max(cd_senha)
	into STRICT	ie_status_w,
		cd_guia_w,
		cd_senha_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_estagio_w
	from	estagio_autorizacao
	where	ie_situacao		= 'A'
	and	ie_interno		= CASE WHEN ie_status_w='1' THEN '10' WHEN ie_status_w='2' THEN '5' WHEN ie_status_w='3' THEN '90' END
	and	coalesce(ie_parcial,'N')	= 'N'
	and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

	if (nr_seq_estagio_w IS NOT NULL AND nr_seq_estagio_w::text <> '') then
		CALL atualizar_autorizacao_convenio(nr_sequencia_autor_w,nm_usuario_p,nr_seq_estagio_w,'N','N','S');
	end if;

	if (ie_status_w  <> '3') then

		update	autorizacao_convenio
		set	cd_autorizacao	= coalesce(cd_guia_w,cd_autorizacao),
			cd_senha		= coalesce(cd_senha_w,cd_senha)
		where	nr_sequencia	= nr_sequencia_autor_w;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
