CREATE OR REPLACE PROCEDURE pls_atualiza_estagio_guia_inte ( nr_seq_guia_p bigint, ie_regra_aud_ptu_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
--IE_REGRA_AUD_PTU_P - Definir se a guia se encaixou nas regras de de auditoria fixa do manual do PTU 
 
cd_senha_w			pls_guia_plano.cd_senha%type;

dt_validade_senha_w		pls_guia_plano.dt_validade_senha%type;
dt_solicitacao_w		pls_guia_plano.dt_solicitacao%type;
dt_validade_w			varchar(20);
ds_observacao_w			pls_guia_plano.ds_observacao%type;

ie_status_w			pls_guia_plano.ie_status%type;
ie_estagio_w			pls_guia_plano.ie_estagio%type;
ie_tipo_processo_w		pls_guia_plano.ie_tipo_processo%type;
ie_tipo_guia_w			pls_guia_plano.ie_tipo_guia%type;
ie_tipo_intercambio_w		pls_guia_plano.ie_tipo_intercambio%type;
ie_libera_intercamb_w		pls_guia_plano_proc.ie_libera_intercambio%type;
ie_status_proc_w		pls_guia_plano_proc.ie_status%type;
ie_identificacao_w		varchar(2);
ie_tipo_contrato_benef_w	varchar(2);
ds_status_w			varchar(255);
ds_estagio_w			varchar(255);
ie_define_se_guia_auditoria_w	varchar(255);
ie_trafega_wsd_w		varchar(2)	:= 'S';

nr_seq_segurado_w		pls_guia_plano.nr_seq_segurado%type;
nr_seq_guia_proc_w		pls_guia_plano_proc.nr_sequencia%type;

qt_auditoria_w			bigint;
qt_glosa_cab_w			bigint;
qt_ocor_cab_w			bigint;
qt_itens_aud_w			bigint;
qt_itens_neg_w			bigint;
qt_itens_aprov_w		bigint;
qt_dias_validade_w		bigint;
qt_itens_pendente_w		bigint;
qt_guia_analise_w		bigint;
qt_reg_aguard_w			bigint;
qt_regra_senha_w		bigint;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_libera_intercambio, 
		ie_status 
	from	pls_guia_plano_proc 
	where	nr_seq_guia	= nr_seq_guia_p;


BEGIN 
 
select	ie_status, 
	ie_estagio, 
	ie_tipo_processo, 
	nr_seq_segurado, 
	dt_solicitacao, 
	ie_tipo_guia, 
	ie_tipo_intercambio 
into STRICT	ie_status_w, 
	ie_estagio_w, 
	ie_tipo_processo_w, 
	nr_seq_segurado_w, 
	dt_solicitacao_w, 
	ie_tipo_guia_w, 
	ie_tipo_intercambio_w 
from	pls_guia_plano 
where	nr_sequencia	= nr_seq_guia_p;
 
--Verificar se existe glosa no cabeçalho da guia 
select	count(1) 
into STRICT	qt_glosa_cab_w 
from	pls_guia_glosa 
where	nr_seq_guia	= nr_seq_guia_p;
 
--Verificar se existe ocorrência para o cabeçalho da guia 
select	count(1) 
into STRICT	qt_ocor_cab_w 
from	pls_ocorrencia_benef 
where	nr_seq_guia_plano	= nr_seq_guia_p 
and	coalesce(nr_seq_proc::text, '') = '' 
and	coalesce(nr_seq_mat::text, '') = '';
 
if (ie_tipo_intercambio_w	= 'E') then 
	ie_identificacao_w	:= 'E';
	 
	if (ie_regra_aud_ptu_p	= 'S') then 
		update	pls_guia_plano_proc 
		set	ie_status	= 'A', 
			qt_autorizada	= 0, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p 
		where	nr_seq_guia	= nr_seq_guia_p;
		 
		update	pls_guia_plano_mat 
		set	ie_status	= 'A', 
			qt_autorizada	= 0, 
			dt_atualizacao	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p 
		where	nr_seq_guia	= nr_seq_guia_p;
		 
		update	pls_guia_plano 
		set	ie_status		= '2', 
			ie_estagio		= 1, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_seq_guia_p;
		 
		select	count(1) 
		into STRICT	qt_auditoria_w 
		from	pls_auditoria 
		where	nr_seq_guia 	= nr_seq_guia_p 
		and	coalesce(dt_liberacao::text, '') = '';
		 
		if (qt_auditoria_w	= 0) then 
			CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
		else 
			update	pls_auditoria_item 
			set	ie_status		= 'P', 
				ie_status_solicitacao	= 'P', 
				dt_atualizacao		= clock_timestamp(), 
				nm_usuario		= nm_usuario_p 
			where	exists (	SELECT	1 
						from	pls_auditoria x 
						where	x.nr_seq_guia	= nr_seq_guia_p 
						and	coalesce(x.dt_liberacao::text, '') = '' 
						and	x.nr_sequencia	= nr_seq_auditoria);
		end if;
			 
		CALL ptu_gerar_grupo_aud_padrao(nr_seq_guia_p, null, 'GA', nm_usuario_p);
		ie_identificacao_w	:= 'E1';
	else 
		if (qt_glosa_cab_w	> 0) and (ie_estagio_w	= 2)then 
			if (pls_obter_acao_ocor_comb_guia(nr_seq_guia_p, 'V') = 'S') then 
				ie_define_se_guia_auditoria_w	:= (pls_obter_acao_ocor_comb_guia(nr_seq_guia_p, 'O'));
			end if;
 
			if (coalesce(ie_define_se_guia_auditoria_w,'X')	<> 'A') then 
				update	pls_guia_plano_proc 
				set	ie_status	= 'N', 
					qt_autorizada	= 0, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p 
				where	nr_seq_guia	= nr_seq_guia_p;
				 
				update	pls_guia_plano_mat 
				set	ie_status	= 'N', 
					qt_autorizada	= 0, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p 
				where	nr_seq_guia	= nr_seq_guia_p;
				 
				update	pls_guia_plano 
				set	ie_status	= '3', 
					ie_estagio	= 4, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p 
				where	nr_sequencia	= nr_seq_guia_p;
			elsif (coalesce(ie_define_se_guia_auditoria_w,'X')	= 'A') then 
				update	pls_guia_plano_proc 
				set	ie_status	= 'A', 
					qt_autorizada	= 0, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p 
				where	nr_seq_guia	= nr_seq_guia_p 
				and	ie_status	<> 'N';
				 
				update	pls_guia_plano_mat 
				set	ie_status	= 'A', 
					qt_autorizada	= 0, 
					dt_atualizacao	= clock_timestamp(), 
					nm_usuario	= nm_usuario_p 
				where	nr_seq_guia	= nr_seq_guia_p 
				and	ie_status	<> 'N';
				 
				update	pls_guia_plano 
				set	ie_status		= '2', 
					ie_estagio		= 1, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				select	count(1) 
				into STRICT	qt_auditoria_w 
				from	pls_auditoria 
				where	nr_seq_guia 	= nr_seq_guia_p 
				and	coalesce(dt_liberacao::text, '') = '';
				 
				if (qt_auditoria_w	> 0) then 
					update	pls_auditoria_item 
					set	ie_status		= 'P', 
						ie_status_solicitacao	= 'P', 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	ie_status_solicitacao	<> 'N' 
					and	exists (	SELECT	1 
								from	pls_auditoria x 
								where	x.nr_seq_guia	= nr_seq_guia_p 
								and	coalesce(x.dt_liberacao::text, '') = '' 
								and	x.nr_sequencia	= nr_seq_auditoria);
				else 
					CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				end if;
			end if;
			 
			ie_identificacao_w	:= 'E2';
		elsif (qt_ocor_cab_w	> 0) and (ie_estagio_w	= 1) then 
			update	pls_guia_plano_proc 
			set	ie_status	= 'A', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p 
			and	ie_status	not in ('N','A');
			 
			update	pls_guia_plano_mat 
			set	ie_status	= 'A', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p 
			and	ie_status	not in ('N','A');
			 
			qt_itens_aud_w	:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'A', 
									null, null, null, 
									null);
			if (qt_itens_aud_w	= 0) then 
				update	pls_guia_plano 
				set	ie_status		= '3', 
					ie_estagio		= 4, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				CALL pls_desfazer_auditoria(nr_seq_guia_p);
			else 
				select	count(1) 
				into STRICT	qt_auditoria_w 
				from	pls_auditoria 
				where	nr_seq_guia 	= nr_seq_guia_p 
				and	coalesce(dt_liberacao::text, '') = '';
				 
				if (qt_auditoria_w	> 0) then 
					update	pls_auditoria_item 
					set	ie_status		= 'P', 
						ie_status_solicitacao	= 'P', 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	ie_status_solicitacao	<> 'N' 
					and	exists (	SELECT	1 
								from	pls_auditoria x 
								where	x.nr_seq_guia	= nr_seq_guia_p 
								and	coalesce(x.dt_liberacao::text, '') = '' 
								and	x.nr_sequencia	= nr_seq_auditoria);
				else 
					CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				end if;
			end if;
			ie_identificacao_w	:= 'E3';
		else 
			if (ie_estagio_w	= 2) then 
				qt_itens_neg_w		:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'N', 
											null, null, null, 
											null);
				qt_itens_aprov_w	:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'S', 
											null, null, null, 
											null);
				qt_itens_aud_w		:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'A', 
											null, null, null, 
											null);
				 
				if (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	> 0) then 
					update	pls_guia_plano 
					set	ie_status		= '2', 
						ie_estagio		= 1, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_sequencia		= nr_seq_guia_p;
					 
					select	count(1) 
					into STRICT	qt_auditoria_w 
					from	pls_auditoria 
					where	nr_seq_guia 	= nr_seq_guia_p 
					and	coalesce(dt_liberacao::text, '') = '';
					 
					if (qt_auditoria_w	> 0) then 
						update	pls_auditoria_item 
						set	ie_status		= 'P', 
							ie_status_solicitacao	= 'P', 
							dt_atualizacao		= clock_timestamp(), 
							nm_usuario		= nm_usuario_p 
						where	ie_status_solicitacao	<> 'N' 
						and	exists (	SELECT	1 
									from	pls_auditoria x 
									where	x.nr_seq_guia	= nr_seq_guia_p 
									and	coalesce(x.dt_liberacao::text, '') = '' 
									and	x.nr_sequencia	= nr_seq_auditoria);
					else 
						CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
					end if;
					 
					ie_identificacao_w	:= 'E4';
				elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	= 0) and (qt_itens_aud_w	> 0) then 
					update	pls_guia_plano 
					set	ie_status		= '2', 
						ie_estagio		= 1, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_sequencia		= nr_seq_guia_p;
					 
					select	count(1) 
					into STRICT	qt_auditoria_w 
					from	pls_auditoria 
					where	nr_seq_guia 	= nr_seq_guia_p 
					and	coalesce(dt_liberacao::text, '') = '';
					 
					if (qt_auditoria_w	> 0) then 
						update	pls_auditoria_item 
						set	ie_status		= 'P', 
							ie_status_solicitacao	= 'P', 
							dt_atualizacao		= clock_timestamp(), 
							nm_usuario		= nm_usuario_p 
						where	ie_status_solicitacao	<> 'N' 
						and	exists (	SELECT	1 
									from	pls_auditoria x 
									where	x.nr_seq_guia	= nr_seq_guia_p 
									and	coalesce(x.dt_liberacao::text, '') = '' 
									and	x.nr_sequencia	= nr_seq_auditoria);
					else 
						CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
					end if;
					 
					ie_identificacao_w	:= 'E5';
				elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	= 0) then 
					SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, 0, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
 
					begin 
						select	to_date(dt_validade_w,'dd/mm/rrrr') 
						into STRICT	dt_validade_senha_w 
						;
					exception 
					when others then 
						dt_validade_senha_w	:= null;
					end;
 
					update	pls_guia_plano 
					set	ie_status		= '1', 
						ie_estagio		= 10, 
						dt_validade_senha	= dt_validade_senha_w, 
						cd_senha		= cd_senha_w, 
						dt_atualizacao		= clock_timestamp(), 
						dt_autorizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p, 
						ds_observacao		= ds_observacao_w 
					where	nr_sequencia		= nr_seq_guia_p;
					 
					ie_identificacao_w	:= 'E6';
				elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	= 0) and (qt_itens_aud_w	= 0) then 
					update	pls_guia_plano 
					set	ie_status		= '3', 
						ie_estagio		= 4, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_sequencia		= nr_seq_guia_p;
					 
					ie_identificacao_w	:= 'E7';
				end if;
			elsif (ie_estagio_w	= 3) then 
				SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, 0, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
 
				begin 
					select	to_date(dt_validade_w,'dd/mm/rrrr') 
					into STRICT	dt_validade_senha_w 
					;
				exception 
				when others then 
					dt_validade_senha_w	:= null;
				end;
 
				update	pls_guia_plano_proc 
				set	ie_status		= 'S', 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_seq_guia		= nr_seq_guia_p;
 
				update	pls_guia_plano_mat 
				set	ie_status		= 'S', 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_seq_guia		= nr_seq_guia_p;
				 
				update	pls_guia_plano 
				set	ie_status		= '1', 
					ie_estagio		= 6, 
					dt_validade_senha	= dt_validade_senha_w, 
					cd_senha		= cd_senha_w, 
					dt_autorizacao		= clock_timestamp(), 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p, 
					ds_observacao		= ds_observacao_w 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				ie_identificacao_w	:= 'E8';
			end if;
		end if;
	end if;
elsif (ie_tipo_intercambio_w	= 'I') then 
	ie_identificacao_w	:= 'I';
	if (pls_obter_se_scs_ativo	= 'A') then 
		if (qt_glosa_cab_w	> 0) then 
			update	pls_guia_plano_proc 
			set	ie_status	= 'N', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p;
			 
			update	pls_guia_plano_mat 
			set	ie_status	= 'N', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p;
			 
			update	pls_guia_plano 
			set	ie_status		= '3', 
				ie_estagio		= 4, 
				dt_atualizacao		= clock_timestamp(), 
				nm_usuario		= nm_usuario_p 
			where	nr_sequencia		= nr_seq_guia_p;
			 
			ie_identificacao_w	:= 'I1';
		elsif (qt_ocor_cab_w	> 0) then 
			update	pls_guia_plano_proc 
			set	ie_status	= 'A', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p 
			and	ie_status	not in ('N','A');
			 
			update	pls_guia_plano_mat 
			set	ie_status	= 'A', 
				qt_autorizada	= 0, 
				dt_atualizacao	= clock_timestamp(), 
				nm_usuario	= nm_usuario_p 
			where	nr_seq_guia	= nr_seq_guia_p 
			and	ie_status	not in ('N','A');
			 
			update	pls_guia_plano 
			set	ie_status		= '2', 
				ie_estagio		= 1, 
				dt_atualizacao		= clock_timestamp(), 
				nm_usuario		= nm_usuario_p 
			where	nr_sequencia		= nr_seq_guia_p;
	 
			CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
			ie_identificacao_w	:= 'I2';
		else 
			qt_itens_neg_w			:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'N', 
											null, null, null, 
											null);
			qt_itens_aprov_w		:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'S', 
											null, null, null, 
											null);
			qt_itens_aud_w			:= pls_obter_qt_itens_status(	nr_seq_guia_p, null, 'A', 
											null, null, null, 
											null);
			ie_tipo_contrato_benef_w	:= substr(pls_obter_dados_segurado(nr_seq_segurado_w, 'TCI'),1,2);
			 
			if (ie_tipo_contrato_benef_w	= 'F') then 
				ie_trafega_wsd_w	:= pls_obter_se_funda_trafega_wsd(nr_seq_segurado_w);
			end if;
 
			if (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	> 0) then 
				if (ie_trafega_wsd_w	= 'S') then 
					update	pls_guia_plano_proc 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
 
					update	pls_guia_plano_mat 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
				end if;
				 
				update	pls_guia_plano 
				set	ie_status		= '2', 
					ie_estagio		= 1, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				ie_identificacao_w	:= 'I3';
			elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	= 0) and (qt_itens_aud_w	> 0) then 
				update	pls_guia_plano 
				set	ie_status		= '2', 
					ie_estagio		= 1, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
 
				CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				ie_identificacao_w	:= 'I4';
			elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	= 0) then 
				if (ie_trafega_wsd_w	= 'S') then 
					update	pls_guia_plano_proc 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
 
					update	pls_guia_plano_mat 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
					 
					update	pls_guia_plano 
					set	ie_status		= '2', 
						ie_estagio		= 12, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_sequencia		= nr_seq_guia_p;
				else 
					SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, 0, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
 
					update	pls_guia_plano 
					set	ie_status		= '1', 
						ie_estagio		= 10, 
						dt_atualizacao		= clock_timestamp(), 
						dt_autorizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p, 
						cd_senha		= cd_senha_w, 
						dt_validade_senha	= dt_validade_w 
					where	nr_sequencia		= nr_seq_guia_p;
				end if;
				ie_identificacao_w	:= 'I5';
			elsif (qt_itens_neg_w	> 0) and (qt_itens_aprov_w	= 0) and (qt_itens_aud_w	= 0) then 
				update	pls_guia_plano 
				set	ie_status		= '3', 
					ie_estagio		= 4, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				ie_identificacao_w	:= 'I6';
			elsif (qt_itens_neg_w	= 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	> 0) then 
				if (ie_trafega_wsd_w	= 'S') then 
					update	pls_guia_plano_proc 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
 
					update	pls_guia_plano_mat 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
				end if;
				 
				update	pls_guia_plano_proc 
				set	qt_autorizada		= 0, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_seq_guia		= nr_seq_guia_p 
				and	ie_status		= 'A';
				 
				update	pls_guia_plano_mat 
				set	qt_autorizada		= 0, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_seq_guia		= nr_seq_guia_p 
				and	ie_status		= 'A';	
				 
				update	pls_guia_plano 
				set	ie_status		= '2', 
					ie_estagio		= 1, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				ie_identificacao_w	:= 'I7';
			elsif (qt_itens_neg_w	= 0) and (qt_itens_aprov_w	= 0) and (qt_itens_aud_w	> 0) then 
				update	pls_guia_plano 
				set	ie_status		= '2', 
					ie_estagio		= 1, 
					dt_atualizacao		= clock_timestamp(), 
					nm_usuario		= nm_usuario_p 
				where	nr_sequencia		= nr_seq_guia_p;
				 
				CALL pls_gerar_auditoria_guia(nr_seq_guia_p, nm_usuario_p);
				ie_identificacao_w	:= 'I8';
			elsif (qt_itens_neg_w	= 0) and (qt_itens_aprov_w	> 0) and (qt_itens_aud_w	= 0) then 
				if (ie_trafega_wsd_w	= 'S') then 
					update	pls_guia_plano_proc 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
 
					update	pls_guia_plano_mat 
					set	ie_status		= 'I', 
						qt_autorizada		= 0, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_seq_guia		= nr_seq_guia_p 
					and	ie_status		= 'S';
					 
					update	pls_guia_plano 
					set	ie_status		= '2', 
						ie_estagio		= 12, 
						dt_atualizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p 
					where	nr_sequencia		= nr_seq_guia_p;
				else 
					SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, 0, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
									 
					update	pls_guia_plano 
					set	ie_status		= '1', 
						ie_estagio		= 6, 
						dt_atualizacao		= clock_timestamp(), 
						dt_autorizacao		= clock_timestamp(), 
						nm_usuario		= nm_usuario_p, 
						cd_senha		= cd_senha_w, 
						dt_validade_senha	= dt_validade_w 
					where	nr_sequencia		= nr_seq_guia_p;
				end if;
				ie_identificacao_w	:= 'I9';
			end if;
		end if;
	else 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_guia_proc_w, 
			ie_libera_intercamb_w, 
			ie_status_proc_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin 
			if (coalesce(ie_libera_intercamb_w,'N')	<> 'S') and (ie_status_proc_w	not in ('A','N')) then 
				update	pls_guia_plano_proc 
				set	ie_status	= 'I', 
					qt_autorizada	= 0			 
				where	nr_sequencia	= nr_seq_guia_proc_w;
				 
				qt_itens_pendente_w	:= qt_itens_pendente_w + 1;
				 
				select	count(1) 
				into STRICT	qt_guia_analise_w 
				from	pls_auditoria 
				where	nr_seq_guia	= nr_seq_guia_p 
				and	coalesce(dt_liberacao::text, '') = '';
				 
				if (qt_guia_analise_w	> 0) then 
					update	pls_auditoria_item 
					set	ie_status_solicitacao	= 'P', 
						ie_status		= 'P' 
					where	nr_seq_proc_origem	= nr_seq_guia_proc_w;
				end if;
			elsif (coalesce(ie_libera_intercamb_w,'N')	<> 'S') then 
				qt_itens_pendente_w	:= qt_itens_pendente_w + 1;
			end if;
			end;
		end loop;
		close C01;
 
		select	count(1) 
		into STRICT	qt_reg_aguard_w 
		from	pls_guia_plano_proc 
		where	nr_seq_guia	= nr_seq_guia_p 
		and	ie_status	= 'I';
 
		if (qt_reg_aguard_w	> 0) and (ie_estagio_w not in (1,2)) then 
			update	pls_guia_plano 
			set	ie_status	= '2', 
				ie_estagio	= 12 
			where	nr_sequencia	= nr_seq_guia_p;
		end if;
		 
		ie_identificacao_w	:= 'II';
	end if;
end if;
 
begin 
	select	substr(obter_valor_dominio(1747,ie_status),1,255), 
		substr(obter_valor_dominio(2055,ie_estagio),1,255) 
	into STRICT	ds_status_w, 
		ds_estagio_w 
	from	pls_guia_plano 
	where	nr_sequencia	= nr_seq_guia_p;
exception 
when others then 
	ds_status_w	:= null;
	ds_estagio_w	:= null;
end;
 
CALL pls_guia_gravar_historico(	nr_seq_guia_p, 7, 'Atualização de estágio de guia de intercâmbio. Estágio: '||ds_estagio_w||' / Status: '||ds_status_w||' / Identificação: '||ie_identificacao_w||' / Tipo de contrato benef: '||ie_tipo_contrato_benef_w, 
				null, nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualiza_estagio_guia_inte ( nr_seq_guia_p bigint, ie_regra_aud_ptu_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

