CREATE OR REPLACE PROCEDURE pls_atualiza_estag_guia_interc ( nr_seq_guia_p bigint, nm_usuario_p text, ie_itens_pendentes_p INOUT text) AS $body$
DECLARE


nr_seq_guia_proc_w		bigint;
ie_libera_intercamb_w		varchar(2);
qt_reg_aguard_w			bigint;
ie_estagio_guia_w		bigint;
ie_status_proc_w		varchar(4);
ie_status_mat_w			varchar(4);
qt_itens_pendente_w		bigint	:= 0;
qt_guia_analise_w		bigint	:= 0;
nr_seq_guia_mat_w		bigint;
qt_proc_aguardo_w		bigint;
qt_mat_aguard_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_libera_intercambio,
		ie_status
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p;


BEGIN

select	count(1)
into STRICT	qt_reg_aguard_w
from	pls_guia_plano_proc
where	nr_seq_guia	= nr_seq_guia_p
and	ie_status	= 'I';

select	ie_estagio
into STRICT	ie_estagio_guia_w
from	pls_guia_plano
where	nr_sequencia	= nr_seq_guia_p;

if (qt_reg_aguard_w	= 0) and (ie_estagio_guia_w not in (1,2)) and (pls_obter_se_scs_ativo	= 'I') then
	ie_itens_pendentes_p	:= 'N';
end if;

end;
$body$
