CREATE OR REPLACE PROCEDURE pls_atualiza_evento_glosa (nm_usuario_p text) AS $body$
DECLARE


nr_seq_motivo_glosa_w	bigint;
qt_glosa_w		bigint;

BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_motivo_glosa_w
from	tiss_motivo_glosa
where	cd_motivo_tiss	=	'1017';

select 	count(*)
into STRICT	qt_glosa_w
from 	pls_glosa_evento
where 	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w
and 	ie_evento	    	= 'CC';

if (qt_glosa_w = 0)	then
	insert	into pls_glosa_evento(  nr_sequencia, dt_atualizacao, nm_usuario,
					nr_seq_motivo_glosa,ie_evento,ie_plano)
		values (	nextval('pls_glosa_evento_seq'), clock_timestamp(),nm_usuario_p,
					nr_seq_motivo_glosa_w, 'CC','S');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
