CREATE OR REPLACE PROCEDURE pls_atualiza_exec_requisicao ( nr_seq_exec_item_p bigint, nr_seq_execucao_p bigint, qt_proc_mat_exec_p bigint, nr_seq_motivo_exec_p bigint, cd_medico_executor_p text, nm_usuario_p text) AS $body$
BEGIN

update	pls_execucao_req_item
set	qt_item			= qt_proc_mat_exec_p,
	nm_usuario		= nm_usuario_p,
