	set	nr_seq_motivo_inclusao	= nr_seq_motivo_exec_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_execucao_p;
end if;

commit;

end;
