CREATE OR REPLACE PROCEDURE pls_atualiza_exec_req_web ( nr_seq_exec_item_p bigint, nr_seq_execucao_p bigint, qt_proc_mat_exec_p bigint, nr_seq_motivo_exec_p bigint, cd_medico_executor_p text, nm_usuario_p text, ds_info_cliente_p text, nr_seq_usuario_web_p bigint) AS $body$
DECLARE

				
qt_item_w			pls_execucao_req_item.qt_item%type;
nr_seq_requisicao_w	pls_execucao_req_item.nr_sequencia%type;
qt_item_new_w		pls_execucao_req_item.qt_item%type;
				

BEGIN

begin
	select	qt_item,
			nr_seq_requisicao
	into STRICT	qt_item_w,
			nr_seq_requisicao_w
	from	pls_execucao_req_item
	where	nr_sequencia	= nr_seq_exec_item_p;
exception
when others then
	nr_seq_requisicao_w := null;
	qt_item_w := null;
end;

qt_item_new_w := qt_proc_mat_exec_p;

if (coalesce(qt_item_new_w::text, '') = '' or qt_item_new_w = 0 ) then
	qt_item_new_w := qt_item_w;
	
	insert	into	pls_log_execucao_req(nr_sequencia, dt_geracao_log, dt_atualizacao,
					     nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
					     nr_seq_usuario_web, nr_seq_requisicao, nr_seq_item_exec,
					     qt_item_atual, qt_item_executado, ds_info_cliente,
					     ds_log)
			            values (nextval('pls_log_execucao_req_seq'), clock_timestamp(), clock_timestamp(),
					     nm_usuario_p, clock_timestamp(), nm_usuario_p,
					     nr_seq_usuario_web_p, nr_seq_requisicao_w, nr_seq_exec_item_p,
					     qt_item_w, qt_proc_mat_exec_p, ds_info_cliente_p,
					     'Log gerado na procedure PLS_ATUALIZA_EXEC_REQ_WEB. Quantidade do item foi passado como vazio ou 0 para ser executado.'
);
					
end if;

commit;

CALL pls_atualiza_exec_requisicao(nr_seq_exec_item_p, nr_seq_execucao_p, qt_item_new_w,
			     nr_seq_motivo_exec_p, cd_medico_executor_p, nm_usuario_p);


end;
