CREATE OR REPLACE PROCEDURE pls_atualiza_guia_fisica ( nr_seq_lote_p pls_protocolo_conta.nr_seq_lote_conta%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Atualizar a informacao de guia fisica do protocolo, atraves da funcionalidade
	"BD - Guia fisica apresentada" da funcao "OPS - Controle de Producao Medica.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------


-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
nr_index_w		integer := 0;
i			integer := 0;
nm_usuario_w 	pls_protocolo_conta.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;

--Cursor dos protocolos caso o parametro seja o lote
C01 CURSOR(nr_seq_lote_pc	pls_protocolo_conta.nr_seq_lote_conta%type) FOR
	SELECT	nr_sequencia,
		coalesce(ie_guia_fisica, 'N') ie_guia_fisica
	from	pls_protocolo_conta
	where	nr_seq_lote_conta = nr_seq_lote_pc
	and	ie_status in ('1', '2', '3', '5', '7');

--Procedure para fazer o update no protocolo via forall
procedure 	tb_nr_sequencia_p	in out	pls_util_cta_pck.t_number_table := atualiza_guia_protocolo(	tb_nr_sequencia_p	in out	pls_util_cta_pck.t_number_table) is;
BEGIN
--Caso tenha algum valor na variavel table, faz o update.
if (tb_nr_sequencia_p.count > 0) then
	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		update 	pls_protocolo_conta set
			ie_guia_fisica = 'S'
		where	nr_sequencia = tb_nr_sequencia_w(i);

	commit;
end if;
--Zera o index e a variavel table
nr_index_p := 0;
tb_nr_sequencia_p.delete;

end;	

--Inicio da rotina
begin
--Caso o parametro seja o lote, deve atualizar todos os protocolos do lote
if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	for r_C01_w in C01(nr_seq_lote_p) loop
		begin
		--Caso a guia fisica seja 'N' guarda a sequencia na variavel table
		if (r_C01_w.ie_guia_fisica = 'N') then
			tb_nr_sequencia_w(nr_index_w) := r_C01_w.nr_sequencia;
		end if;
		
			CALL pls_gravar_hist_prot_conta(
			r_C01_w.nr_sequencia,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			'N',
			'S',
			null,
			null,
			null,
			null,
			clock_timestamp(),
			nm_usuario_w,
			null);
		
		--Se alcancar o quantidade de registro por transacao chama a rotina e realiza o update
		if (nr_index_w >= pls_util_pck.qt_registro_transacao_w) then
			tb_nr_sequencia_w := atualiza_guia_protocolo(tb_nr_sequencia_w);			
		else
		--Caso nao soma o contador
			nr_index_w := nr_index_w + 1;
		end if;
		end;
	end loop;
	--Caso tenha sobrado registros na variavel table, faz o update dos mesmos
	tb_nr_sequencia_w := atualiza_guia_protocolo(tb_nr_sequencia_w);

--Caso o parametro seja o protocolo ira realizar um update simples restringindo pela PK
elsif (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	
	update	pls_protocolo_conta set
		ie_guia_fisica = 'S'
	where	nr_sequencia = nr_seq_protocolo_p;
	
	CALL pls_gravar_hist_prot_conta(
			nr_seq_protocolo_p,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			null,
			'N',
			'S',
			null,
			null,
			null,
			null,
			clock_timestamp(),
			nm_usuario_w,
			null);
			
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
