CREATE OR REPLACE PROCEDURE pls_atualiza_guia_imp_xml ( ie_opcao_p text, nr_seq_guia_p bigint, nm_prestador_imp_p text, cd_cnes_imp_p text, nr_seq_prestador_imp_p bigint, cd_cgc_prestador_imp_p text, cd_cpf_prestador_imp_p text, nm_usuario_p text) AS $body$
