SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_atualiza_guia_ocor_comb ( nr_seq_guia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar os itens da guia conforme a geração de ocorr~encia combinada.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_guia_proc_w			bigint;
nr_seq_guia_mat_w			bigint;
qt_reg_proc_auditoria_w			bigint	:= 0;
qt_reg_mat_auditoria_w			bigint	:= 0;
qt_reg_proc_glosa_w			bigint	:= 0;
qt_reg_mat_glosa_w			bigint	:= 0;
qt_reg_negados_w			bigint	:= 0;
qt_reg_analise_w			bigint	:= 0;
qt_reg_auditoria_w			bigint	:= 0;
qt_reg_glosa_w				bigint	:= 0;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_guia_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(1)
	into STRICT	qt_reg_proc_glosa_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_seq_proc	= nr_seq_guia_proc_w
	and	coalesce(nr_seq_mat::text, '') = ''
	and	ie_tipo		= 'G';

	select	count(1)
	into STRICT	qt_reg_proc_auditoria_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_seq_proc	= nr_seq_guia_proc_w
	and	coalesce(nr_seq_mat::text, '') = ''
	and	ie_tipo		= 'O';

	if (qt_reg_proc_glosa_w	> 0) then
		update	pls_guia_plano_proc
		set	ie_status	= 'N',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_guia_proc_w;

		qt_reg_negados_w	:= qt_reg_negados_w + 1;
	elsif (qt_reg_proc_auditoria_w	> 0) then
		update	pls_guia_plano_proc
		set	ie_status	= 'A',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_guia_proc_w;

		qt_reg_analise_w	:= qt_reg_analise_w + 1;
	end if;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_guia_mat_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	select	count(1)
	into STRICT	qt_reg_mat_glosa_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_seq_mat	= nr_seq_guia_mat_w
	and	coalesce(nr_seq_proc::text, '') = ''
	and	ie_tipo		= 'G';

	select	count(1)
	into STRICT	qt_reg_mat_auditoria_w
	from	pls_guia_glosa_ocorrencia_v
	where	nr_seq_mat	= nr_seq_guia_mat_w
	and	coalesce(nr_seq_proc::text, '') = ''
	and	ie_tipo		= 'O';

	if (qt_reg_mat_glosa_w	> 0) then
		update	pls_guia_plano_mat
		set	ie_status	= 'N',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_guia_mat_w;

		qt_reg_negados_w	:= qt_reg_negados_w + 1;
	elsif (qt_reg_mat_auditoria_w	> 0) then
		update	pls_guia_plano_mat
		set	ie_status	= 'A',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_seq_guia_mat_w;

		qt_reg_analise_w	:= qt_reg_analise_w + 1;
	end if;
	end;
end loop;
close C02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_atualiza_guia_ocor_comb ( nr_seq_guia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

