CREATE OR REPLACE PROCEDURE pls_atualiza_guia_prest_req ( nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_guia_plano_imp_p pls_guia_plano_imp.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualiza o número da guia enviado pelo prestador quando já existir uma requisição
na base e a mesma for executada.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ x ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_guia_prestador_w		pls_guia_plano_imp.cd_guia_prestador%type;


BEGIN

--Só executa a atualização se houver sequencia da importação informada
if (nr_seq_guia_plano_imp_p IS NOT NULL AND nr_seq_guia_plano_imp_p::text <> '') then
	select	cd_guia_prestador
	into STRICT	cd_guia_prestador_w
	from	pls_guia_plano_imp
	where	nr_sequencia = nr_seq_guia_plano_imp_p;

	update	pls_requisicao
	set	cd_guia_prestador = coalesce(cd_guia_prestador, cd_guia_prestador_w)
	where	nr_sequencia	  = nr_seq_requisicao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
