CREATE OR REPLACE PROCEDURE pls_atualiza_info_anvisa_mat ( cd_material_p material.cd_material%type, nr_registro_anvisa_p material.nr_registro_anvisa%type, dt_validade_reg_anvisa_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_mat_estab_w			material_estab.nr_sequencia%type;
dt_validade_reg_anvisa_w	material.dt_validade_reg_anvisa%type;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_mat_estab_w
from	material_estab
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_material		= cd_material_p;

if (coalesce(nr_seq_mat_estab_w::text, '') = '') then
	begin
	dt_validade_reg_anvisa_w := to_date(substr(trim(both dt_validade_reg_anvisa_p),1,10));
	exception
	when others then
		dt_validade_reg_anvisa_w := null;
	end;

	insert into material_estab(nr_sequencia,			cd_estabelecimento,	cd_material,
		dt_atualizacao,			nm_usuario,		ie_baixa_estoq_pac,
		ie_material_estoque,		nr_minimo_cotacao,	ie_ressuprimento,
		dt_atualizacao_nrec,		nm_usuario_nrec,	ie_classif_custo,
		ie_prescricao,			ie_padronizado,		ie_estoque_lote,
		ie_requisicao,			ie_controla_serie,	nr_registro_anvisa,
		dt_validade_reg_anvisa,ie_vigente_anvisa)
	values (nextval('material_estab_seq'),	cd_estabelecimento_p,	cd_material_p,
		clock_timestamp(),			nm_usuario_p,		'S',
		'S',				1,			'S',
		clock_timestamp(),			nm_usuario_p,		'B',
		'S',				'N',			'N',
		'S',				'N',			nr_registro_anvisa_p,
		dt_validade_reg_anvisa_w,substr(coalesce(obter_exigencia_anvisa_mat(cd_estabelecimento_p,cd_material_p,'VI'),'N'),1,1));

	commit;
end if;

end;
$body$
