CREATE OR REPLACE PROCEDURE pls_atualiza_info_pgto_reem ( ie_resp_credito_p text, cd_pessoa_resp_p text, cd_condicao_pgto_p text, ie_forma_pgto_p text, cd_banco_p text, cd_agencia_p text, ie_dig_agencia_p text, cd_conta_p text, ie_dig_conta_p text, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia %type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atuliza as informações de pagamento dos protocolos de reembolso
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
-------------------------------------------------------------------------------------------------------------------
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	update	pls_protocolo_conta
	set	ie_tipo_resp_credito 	= ie_resp_credito_p,
		cd_condicao_pagamento	= cd_condicao_pgto_p,
		ie_forma_pagamento 	= ie_forma_pgto_p,
		cd_banco 		= cd_banco_p,
		cd_agencia_bancaria 	= cd_agencia_p,
		ie_digito_agencia 	= ie_dig_agencia_p,
		cd_conta 		= cd_conta_p,
		ie_digito_conta 	= ie_dig_conta_p
	where	nr_sequencia 		= nr_seq_protocolo_p;

	if (ie_resp_credito_p = 'PI') then
		update 	pls_protocolo_conta
		set	cd_pessoa_fisica 	= cd_pessoa_resp_p
		where	nr_sequencia 		= nr_seq_protocolo_p;
	end if;
end if;

commit;

end;
$body$
